// src/pages/About.js
import React, { useState } from 'react';
import styled from 'styled-components';
import SystemInfo from '../components/SystemInfo';
import Specs from '../components/Specs';
import Team from '../components/Team';

const Container = styled.div`
  padding: 40px 20px 60px;
  max-width: 1100px;
  margin: 0 auto;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: center;

  @media (max-width: 768px) {
    padding: 24px 12px 40px;
  }
`;

const Title = styled.h1`
  font-size: 2.1rem;
  font-weight: 700;
  text-align: center;
  margin: 0 0 16px;
  width: 100%;
  color: ${({ theme }) => theme.colors.text.primary};

  @media (max-width: 768px) {
    font-size: 1.8rem;
  }
`;

const Subtitle = styled.p`
  margin: 0 0 24px;
  max-width: 720px;
  text-align: center;
  font-size: 0.96rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

/* Wrapper to keep tab strip centered */
const TabsOuter = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  margin-bottom: 26px;
`;

/* Glassy pill tab strip */
const TabContainer = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 0.25rem;
  padding: 4px;
  border-radius: 999px;
  background: rgba(15, 23, 42, 0.8);
  border: 1px solid rgba(148, 163, 184, 0.5);
  box-shadow:
    0 18px 40px rgba(15, 23, 42, 0.95),
    inset 0 1px 0 rgba(255, 255, 255, 0.04);

  @media (max-width: 480px) {
    width: 100%;
    justify-content: space-between;
  }
`;

const Tab = styled.button`
  padding: 0.55rem 1.4rem;
  border-radius: 999px;
  border: none;
  outline: none;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 600;
  min-width: 110px;
  text-align: center;

  /* active vs inactive look */
  background: ${props =>
    props.active
      ? 'linear-gradient(180deg,#3b82f6,#2563eb)'
      : 'transparent'};
  color: ${props =>
    props.active ? '#ffffff' : 'rgba(226,232,240,0.9)'};

  box-shadow: ${props =>
    props.active
      ? '0 14px 34px rgba(37,99,235,0.6)'
      : 'none'};

  /* ❌ no hover animation – keep things calm */
  &:hover {
    background: ${props =>
      props.active
        ? 'linear-gradient(180deg,#3b82f6,#2563eb)'
        : 'transparent'};
    box-shadow: ${props =>
      props.active
        ? '0 14px 34px rgba(37,99,235,0.6)'
        : 'none'};
    transform: none;
  }

  @media (max-width: 480px) {
    flex: 1;
    min-width: 0;
  }
`;

/* Big glass section, same feel as History slabs */
const ContentContainer = styled.div`
  width: 100%;
  max-width: 1000px;
  margin: 0 auto;
  padding: 26px 26px 24px;
  border-radius: 22px;

  background: rgba(15, 23, 42, 0.9);
  backdrop-filter: blur(14px) saturate(120%);
  -webkit-backdrop-filter: blur(14px) saturate(120%);
  border: 1px solid rgba(148, 163, 184, 0.4);
  box-shadow:
    0 26px 70px rgba(15, 23, 42, 0.98),
    inset 0 1px 0 rgba(255, 255, 255, 0.03);

  display: flex;
  flex-direction: column;
  align-items: stretch;
  color: ${({ theme }) => theme.colors.text.primary};

  @media (max-width: 768px) {
    padding: 18px 16px 18px;
  }
`;

const About = () => {
  const [activeTab, setActiveTab] = useState('overview');

  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return <SystemInfo />;
      case 'specs':
        return <Specs />;
      case 'team':
        return <Team />;
      default:
        return <SystemInfo />;
    }
  };

  return (
    <Container>
      <Title>About Smart Water Quality Monitoring System</Title>
      <Subtitle>
        A real-time ESP32-based IoT project that measures water quality
        parameters, helps classify pollution, and stores only the readings
        you choose in Firebase.
      </Subtitle>

      <TabsOuter>
        <TabContainer>
          <Tab
            active={activeTab === 'overview'}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </Tab>
          <Tab
            active={activeTab === 'specs'}
            onClick={() => setActiveTab('specs')}
          >
            Specifications
          </Tab>
          <Tab
            active={activeTab === 'team'}
            onClick={() => setActiveTab('team')}
          >
            Our Team
          </Tab>
        </TabContainer>
      </TabsOuter>

      <ContentContainer>{renderContent()}</ContentContainer>
    </Container>
  );
};

export default About;
