// src/theme.js
export const theme = {
  colors: {
    primary: '#2563eb',
    primaryDark: '#1f4ed8',
    secondary: '#10b981',
    background: '#071122',
    surface: '#ffffff',
    text: {
      primary: 'rgba(255,255,255,0.95)',   // increased contrast
      secondary: 'rgba(255,255,255,0.78)',
      muted: 'rgba(255,255,255,0.65)'
    },
    status: {
      success: '#10b981',
      error: '#ef4444',
      warning: '#f59e0b'
    }
  },
  ui: {
    // more opaque glass so background shows less through
    glassBg: 'rgba(255,255,255,0.11)',     // increased from 0.06 -> 0.11
    glassBgLight: 'rgba(255,255,255,0.14)',
    cardBorder: 'rgba(255,255,255,0.14)',
    // stronger, layered shadow for depth
    cardShadow: '0 12px 30px rgba(2,6,23,0.32), inset 0 1px 0 rgba(255,255,255,0.02)',
    blur: '10px',
    // softer accent tones (muted)
    accent: '#60a5fa',      // softer blue
    accentAlt: '#06b6d4',   // teal
    accentSoft: '#7c3aed',  // lilac/purple softer
    success: '#10b981',
    danger: '#ef4444',
    neutral: '#94a3b8'
  },
  typography: {
    fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif",
    h1: { fontSize: '2.5rem', fontWeight: 700 },
    h2: { fontSize: '2rem', fontWeight: 700 },
    body1: { fontSize: '1rem', fontWeight: 500 },
    body2: { fontSize: '0.95rem', fontWeight: 500 }
  },
  spacing: {
    xs: '0.25rem',
    sm: '0.5rem',
    md: '1rem',
    lg: '1.5rem',
    xl: '2rem',
    xxl: '3rem'
  },
  borderRadius: {
    sm: '6px',
    md: '10px',
    lg: '14px',
    xl: '20px'
  },
  breakpoints: {
    mobile: '480px',
    tablet: '768px',
    desktop: '1024px'
  }
};
