// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { ref, push, set, query, orderByChild, limitToLast, onValue as onDbValue } from 'firebase/database';
import { database } from '../firebase';
import { NotificationManager } from '../components/Notification';
import { CardTitle, ValueDisplay } from '../components/Card';
import Gauge from '../components/Gauge';
import { useNavigate } from 'react-router-dom';

/* Container and layout */
const Container = styled.div`
  padding: 36px 28px;
  max-width: 1280px;
  margin: 0 auto;
`;

/* ... (styles are kept same as you provided) ... */
const Header = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom: 26px;
`;

const PageTitle = styled.h2`
  margin: 0;
  color: ${({theme}) => theme.colors.text.primary};
  font-size: 2rem;
`;

const StatusBadge = styled.div`
  padding: 10px 16px;
  border-radius: 999px;
  font-weight: 800;
  display:inline-flex;
  align-items:center;
  gap: 8px;
  background: ${({connected}) => connected ? 'linear-gradient(90deg, rgba(16,185,129,0.12), rgba(16,185,129,0.06))' : 'linear-gradient(90deg, rgba(239,68,68,0.12), rgba(239,68,68,0.06))'};
  color: ${({connected}) => connected ? '#064e3b' : '#7f1d1d'};
  border: 1px solid ${({connected}) => connected ? 'rgba(16,185,129,0.22)' : 'rgba(239,68,68,0.22)'};
  box-shadow: 0 8px 28px rgba(2,6,23,0.28);
  backdrop-filter: blur(6px);
`;

/* Grid / tiles */
const Grid = styled.div`
  display:grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 22px;
  margin-bottom: 22px;
  @media (max-width: 1100px) { grid-template-columns: repeat(2, 1fr); }
  @media (max-width: 680px) { grid-template-columns: 1fr; }
`;

const Tile = styled.div`
  background: rgba(0,0,0,0.18);
  border-radius: ${({theme}) => theme.borderRadius?.lg || '12px'};
  padding: 28px;
  box-shadow: 0 18px 44px rgba(2,6,23,0.30), inset 0 1px 0 rgba(255,255,255,0.02);
  border: 1px solid rgba(255,255,255,0.06);
  backdrop-filter: blur(9px) saturate(120%);
  -webkit-backdrop-filter: blur(9px);
  transition: transform 180ms ease, box-shadow 200ms ease;
  min-height: 220px;
  display:flex;
  flex-direction:column;
  align-items:center;
  justify-content:center;
  color: ${({theme}) => theme.colors.text.primary};
  position: relative;
`;

const Accent = styled.div`
  position:absolute;
  top:16px;
  left:18px;
  width:64px;
  height:8px;
  border-radius:8px;
  background: linear-gradient(90deg, ${p => p.colorStart || '#06b6d4'}, ${p => p.colorEnd || '#22c1d1'});
  opacity: 1;
  z-index: 6;
  box-shadow: 0 8px 18px rgba(2,6,23,0.18);
`;

const TileWrapper = styled.div`
  position: relative;
`;

const BigTitle = styled(CardTitle)`
  font-size: 1.15rem;
  margin-bottom: 6px;
`;

const BigValue = styled(ValueDisplay)`
  font-size: 2.2rem;
  color: ${({theme}) => theme.colors.text.primary};
`;

/* Actions row / input / buttons */
const ActionsRow = styled.div`
  display:flex;
  gap: 14px;
  align-items:center;
  margin-top: 18px;
  width: 100%;

  @media (max-width: 680px) {
    flex-direction: column;
    align-items: stretch;
  }
`;

const SourceInput = styled.input`
  flex: 1;
  padding: 16px 18px;
  border-radius: 14px;
  border: 1px solid rgba(255,255,255,0.08);
  background: rgba(255,255,255,0.98);
  font-size: 1rem;
  box-shadow: inset 0 2px 8px rgba(0,0,0,0.04);
`;

const ButtonsGroup = styled.div`
  display:flex;
  gap: 12px;
  align-items:center;
  @media (max-width: 680px) { width: 100%; justify-content: space-between; }
`;

const PrimaryButton = styled.button`
  background: linear-gradient(180deg,#3b82f6,#2563eb);
  color:white;
  padding: 12px 20px;
  border-radius: 14px;
  border: 1px solid rgba(255,255,255,0.12);
  font-weight:800;
  min-width:170px;
  cursor:pointer;
  box-shadow: 0 10px 30px rgba(37,99,235,0.14);
  &:disabled { opacity: 0.6; cursor:not-allowed; box-shadow:none; }
`;

const SaveButton = styled(PrimaryButton)`
  background: linear-gradient(180deg,#2dd4bf,#10b981);
  box-shadow: 0 10px 30px rgba(16,185,129,0.12);
`;

/* Manual card / predicted bar */
const ManualCard = styled(Tile)`
  gap: 14px;
  cursor: default;
  text-align:center;
  min-height: 220px;
`;

const ManualButton = styled.button`
  background: linear-gradient(180deg,#7c3aed,#6b21a8);
  color: white;
  padding: 12px 16px;
  border-radius: 12px;
  border: 1px solid rgba(255,255,255,0.12);
  font-weight: 700;
  cursor: pointer;
  box-shadow: 0 12px 28px rgba(124,58,237,0.12);
`;

const PredictedBar = styled.div`
  margin-top: 20px;
  padding: 14px 20px;
  display:flex;
  justify-content:space-between;
  align-items:center;
  border-radius: 12px;
  background: rgba(0,0,0,0.18);
  border: 1px solid rgba(255,255,255,0.06);
  box-shadow: 0 12px 30px rgba(2,6,23,0.28);
  color: ${({theme}) => theme.colors.text.primary};
`;

/* ----------- Component ----------- */
function Dashboard() {
  const navigate = useNavigate();

  // sensorData shape: temp, tds, ntu, do, pH
  const [sensorData, setSensorData] = useState({ temp: 0, tds: 0, ntu: 0, do: 0, pH: 7.0 });
  const [connected, setConnected] = useState(false);
  const [source, setSource] = useState('');
  const [pollutionLabel, setPollutionLabel] = useState('');
  const [isClassifying, setIsClassifying] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [notifications, setNotifications] = useState([]);

  // notifications helpers
  const addNotification = (message, type = 'success') =>
    setNotifications(prev => [...prev, { message, type }]);
  const removeNotification = (i) =>
    setNotifications(prev => prev.filter((_, idx) => idx !== i));

  // 1) Subscribe to Firebase readings -> latest reading (limitToLast(1))
  useEffect(() => {
    const readingsRef = query(ref(database, 'readings'), orderByChild('timestamp'), limitToLast(1));
    const unsubscribe = onDbValue(readingsRef, (snap) => {
      const v = snap.val();
      if (!v) return;
      // v is object with one push-id -> record
      const key = Object.keys(v)[0];
      const last = v[key];
      // prefer trusted numeric keys; convert values
      setSensorData(prev => ({
        temp: Number(last.temperature ?? last.temp ?? prev.temp ?? 0),
        tds: Number(last.tds ?? prev.tds ?? 0),
        ntu: Number(last.turbidity ?? last.ntu ?? last.turbidityLinear ?? prev.ntu ?? 0),
        do: Number(last.dissolvedOxygen ?? last.do ?? prev.do ?? 0),
        pH: Number(last.pH ?? prev.pH ?? 7.0)
      }));
      // mark as connected if we have DB value
      setConnected(true);
    });

    return () => unsubscribe();
  }, []);

  // 2) Also poll ESP endpoint as a fallback (keeps UI responsive if DB has no data)
  useEffect(() => {
    let mounted = true;
    const fetchFromESP = async () => {
      try {
        const hostname = window.location.hostname;
        const res = await fetch(`http://${hostname}:5000/api/data`);
        if (!res.ok) throw new Error('no esp');
        const d = await res.json();
        if (!mounted) return;
        // only update if db hasn't provided data recently (we still overwrite)
        setSensorData(prev => ({
          temp: Number(d.temp ?? prev.temp),
          tds: Number(d.tds ?? prev.tds),
          ntu: Number(d.ntu ?? prev.ntu),
          do: Number(d.do ?? prev.do),
          pH: Number(d.pH ?? prev.pH)
        }));
        setConnected(true);
      } catch (err) {
        // if DB subscription already set connected true, keep it; otherwise show disconnected
        setConnected(prev => prev); // no-op
      }
    };

    fetchFromESP();
    const id = setInterval(fetchFromESP, 2000);
    return () => { mounted = false; clearInterval(id); };
  }, []);

  const classifyLocal = ({ tds, pH, ntu, do: DO, temp }) => {
    if (DO > 7 && tds < 300 && ntu < 5 && pH >= 6.5 && pH <= 8.5) return "Good Water";
    if (DO < 3 && ntu > 10) return "Sewage";
    if (tds > 500 && ntu > 10 && pH > 7.5) return "Agricultural Runoff";
    if (tds > 1000 || pH < 6 || pH > 9) return "Chemical Intrusion";
    if (temp > 30 && DO < 6) return "Thermal Pollution";
    return "Good Water";
  };

  const handleClassify = async () => {
    setIsClassifying(true);
    try {
      const label = classifyLocal(sensorData);
      setPollutionLabel(label);
      addNotification(`Classified: ${label}`, 'success');
    } catch (err) {
      addNotification('Classification failed', 'error');
    } finally {
      setIsClassifying(false);
    }
  };

  const handleSave = async () => {
    if (!pollutionLabel) { addNotification('Please classify before saving', 'error'); return; }
    if (!source.trim()) { addNotification('Please enter water source', 'error'); return; }

    setIsSaving(true);
    try {
      const readingsRef = ref(database, 'readings');
      const newRef = push(readingsRef);
      await set(newRef, {
        temperature: Number(sensorData.temp),
        tds: Number(sensorData.tds),
        turbidity: Number(sensorData.ntu),
        dissolvedOxygen: Number(sensorData.do),
        pH: Number(sensorData.pH),
        pollutionLabel,
        source: source.trim(),
        timestamp: new Date().toISOString()
      });
      addNotification('Saved reading to Firebase', 'success');
      setSource('');
    } catch (err) {
      console.error(err);
      addNotification('Save failed', 'error');
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <Container>
      <Header>
        <PageTitle>Current Sensor Readings</PageTitle>
        <StatusBadge connected={connected}>
          {connected ? 'Connected to ESP32 / RTDB' : '⚠ Connection Error'}
        </StatusBadge>
      </Header>

      <Grid>
        <TileWrapper>
          <Accent colorStart="#06b6d4" colorEnd="#22c1d1" />
          <Tile>
            <BigTitle>Temperature</BigTitle>
            <div style={{ display:'flex', flexDirection:'column', alignItems:'center', gap:12 }}>
              <div style={{ width: 200 }}>
                <Gauge value={Number(sensorData.temp)} min={-10} max={50} unit="°C" />
              </div>
              <BigValue>{Number(sensorData.temp).toFixed(2)} °C</BigValue>
            </div>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#60a5fa" colorEnd="#3b82f6" />
          <Tile>
            <BigTitle>TDS</BigTitle>
            <BigValue>{Number(sensorData.tds).toFixed(2)} ppm</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#7c3aed" colorEnd="#9f7aea" />
          <Tile>
            <BigTitle>Turbidity (Quadratic)</BigTitle>
            <BigValue>{Number(sensorData.ntu).toFixed(2)} NTU</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#10b981" colorEnd="#34d399" />
          <Tile>
            <BigTitle>Dissolved O2</BigTitle>
            <BigValue>{Number(sensorData.do).toFixed(2)} mg/L</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#60a5fa" colorEnd="#38bdf8" />
          <Tile>
            <BigTitle>pH (read-only)</BigTitle>
            <BigValue>{Number(sensorData.pH).toFixed(2)}</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#f472b6" colorEnd="#fb7185" />
          <ManualCard>
            <BigTitle>Manual Test</BigTitle>
            <div style={{ color: 'rgba(255,255,255,0.92)' }}>Enter custom parameters on the test page</div>
            <ManualButton onClick={() => navigate('/test-input')}>Open Test Input</ManualButton>
          </ManualCard>
        </TileWrapper>
      </Grid>

      <ActionsRow>
        <SourceInput
          placeholder="Enter water source (e.g., tap, lake, well)"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />

        <ButtonsGroup>
          <PrimaryButton onClick={handleClassify} disabled={isClassifying}>
            {isClassifying ? 'Classifying...' : 'Classify Pollution'}
          </PrimaryButton>

          <SaveButton onClick={handleSave} disabled={isSaving || !pollutionLabel}>
            {isSaving ? 'Saving...' : 'Save to Firebase'}
          </SaveButton>
        </ButtonsGroup>
      </ActionsRow>

      {pollutionLabel && (
        <PredictedBar>
          <div style={{ color: 'rgba(255,255,255,0.95)' }}><strong>Predicted:</strong> &nbsp; <span style={{ fontSize: 16 }}>{pollutionLabel}</span></div>
          <div style={{ color: 'rgba(255,255,255,0.75)' }}>{new Date().toLocaleString()}</div>
        </PredictedBar>
      )}

      <NotificationManager notifications={notifications} onRemove={removeNotification} />
    </Container>
  );
}

export default Dashboard;
