// client/src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ref, push, set } from 'firebase/database';
import { database } from '../firebase';
import { NotificationManager } from '../components/Notification';
import Gauge from '../components/Gauge';

const DashboardContainer = styled.div`
  padding: ${props => props.theme.spacing.md};
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
`;

const Card = styled.div`
  background: #ffffff;
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.xl};
  margin-bottom: ${props => props.theme.spacing.xl};
  box-shadow: 0 4px 6px rgba(0,0,0,0.08);
  border: 1px solid rgba(0,0,0,0.05);
`;

const CardHeader = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom: ${props => props.theme.spacing.xl};
`;

const Title = styled.h2`
  color: ${props => props.theme.colors.text.primary};
  margin: 0;
`;

const StatusBadge = styled.div`
  display:inline-flex;
  align-items:center;
  padding: 6px 12px;
  border-radius: 6px;
  background-color: ${props => props.connected ? '#22c55e' : '#ef4444'};
  color: white;
  font-weight: 600;
  font-size: 14px;
`;

const DataGrid = styled.div`
  display:grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 20px;
  margin-bottom: 20px;
`;

const DataItem = styled.div`
  background: #fff;
  border-radius: 12px;
  border: 1px solid rgba(0,0,0,0.05);
  padding: 20px;
  display:flex;
  flex-direction:column;
  align-items:center;
  justify-content:center;
  height: 180px;
`;

const Label = styled.div`
  font-size: 14px;
  color: #64748b;
  margin-bottom: 10px;
`;

const Value = styled.div`
  font-size: 28px;
  font-weight: 700;
  color: #2563eb;
`;

const InputGroup = styled.div`
  display:flex;
  gap: 20px;
  margin-top: 20px;
  align-items:center;

  @media(max-width: 600px) {
    flex-direction: column;
    width: 100%;
  }
`;

const Input = styled.input`
  padding: 16px;
  border-radius: 8px;
  border: 1px solid rgba(0,0,0,0.1);
  flex: 1;
  font-size: 16px;
`;

const Button = styled.button`
  padding: 16px 28px;
  background: #2563eb;
  color: white;
  font-weight: 600;
  border-radius: 8px;
  border: none;
  cursor: pointer;

  &:disabled {
    background: #94a3b8;
  }
`;

const SecondaryButton = styled(Button)`
  background: #10b981;
`;

const ResultBox = styled.div`
  margin-top: 20px;
  padding: 16px;
  background: #f8fafc;
  border-radius: 12px;
  font-size: 18px;
  display:flex;
  justify-content: space-between;
  align-items:center;
  color:#1e293b;
`;

function Dashboard() {
  const [sensorData, setSensorData] = useState({
    temp: 0,
    tds: 0,
    do: 0,
    ntu: 0,
    pH: 7.0
  });

  const [source, setSource] = useState('');
  const [error, setError] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const [pollutionLabel, setPollutionLabel] = useState('');
  const [pollutionProbs, setPollutionProbs] = useState({});
  const [isClassifying, setIsClassifying] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const addNotification = (msg, type='success') =>
    setNotifications(prev => [...prev, { message: msg, type }]);

  const removeNotification = (i) =>
    setNotifications(prev => prev.filter((_, index) => index !== i));

  useEffect(() => {
    let isMounted = true;
    const fetchData = async () => {
      try {
        const hostname = window.location.hostname;
        const res = await fetch(`http://${hostname}:5000/api/data`);
        if (!res.ok) throw new Error('Server error');
        const data = await res.json();

        if (isMounted) {
          setSensorData(prev => ({
            temp: parseFloat(data.temp ?? prev.temp),
            tds: parseFloat(data.tds ?? prev.tds),
            do: parseFloat(data.do ?? prev.do),
            ntu: parseFloat(data.ntu ?? prev.ntu),
            pH: parseFloat(data.pH ?? prev.pH)
          }));
          setConnectionStatus('Connected to ESP32');
        }
      } catch (err) {
        if (isMounted) {
          setError('Failed to fetch sensor data. Please check ESP32 connection.');
          setConnectionStatus('Connection Error');
        }
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);

    return () => { isMounted = false; clearInterval(interval); };
  }, []);

  // Local JS classifier
  const classifyLocal = ({ tds, pH, ntu, do: DO, temp }) => {
    if (DO > 7 && tds < 300 && ntu < 5 && pH >= 6.5 && pH <= 8.5) return "Good Water";
    if (DO < 3 && ntu > 10) return "Sewage";
    if (tds > 500 && ntu > 10 && pH > 7.5) return "Agricultural Runoff";
    if (tds > 1000 || pH < 6 || pH > 9) return "Chemical Intrusion";
    if (temp > 30 && DO < 6) return "Thermal Pollution";
    return "Good Water";
  };

  const handleClassify = () => {
    setIsClassifying(true);
    const result = classifyLocal(sensorData);
    setPollutionLabel(result);
    addNotification(`Pollution Classified: ${result}`, "success");
    setIsClassifying(false);
  };

  const handleSave = async () => {
    if (!pollutionLabel) {
      addNotification("Classify pollution before saving!", "error");
      return;
    }

    if (!source.trim()) {
      addNotification("Enter water source!", "error");
      return;
    }

    setIsSaving(true);
    try {
      const refNode = ref(database, "readings");
      const newNode = push(refNode);
      await set(newNode, {
        temperature: sensorData.temp,
        tds: sensorData.tds,
        turbidity: sensorData.ntu,
        dissolvedOxygen: sensorData.do,
        pH: sensorData.pH,
        pollutionLabel,
        source,
        timestamp: new Date().toISOString()
      });
      addNotification("Saved to Firebase!", "success");
      setSource('');
    } catch (err) {
      addNotification("Error saving!", "error");
    }
    setIsSaving(false);
  };

  return (
    <DashboardContainer>
      <Card>
        <CardHeader>
          <Title>Current Sensor Readings</Title>
          <StatusBadge connected={connectionStatus === 'Connected to ESP32'}>
            {connectionStatus}
          </StatusBadge>
        </CardHeader>

        {error && <div style={{ color: "#dc2626", marginBottom: 20 }}>{error}</div>}

        <DataGrid>
          <DataItem>
            <Label>Temperature</Label>
            <Gauge
              value={sensorData.temp}
              min={0}
              max={50}
              unit="Â°C"
              warningThreshold={60}
              dangerThreshold={85}
            />
          </DataItem>

          <DataItem>
            <Label>TDS</Label>
            <Value>{sensorData.tds.toFixed(2)} ppm</Value>
          </DataItem>

          <DataItem>
            <Label>Turbidity (Quadratic)</Label>
            <Value>{sensorData.ntu.toFixed(2)} NTU</Value>
          </DataItem>

          <DataItem>
            <Label>Dissolved Oxygen</Label>
            <Value>{sensorData.do.toFixed(2)} mg/L</Value>
          </DataItem>

          <DataItem>
            <Label>pH</Label>
            <Value>{sensorData.pH.toFixed(2)}</Value>
          </DataItem>
        </DataGrid>

        <InputGroup>
          <Input
            placeholder="Enter water source (e.g., tap, lake, well)"
            value={source}
            onChange={(e) => setSource(e.target.value)}
          />

          <Button onClick={handleClassify} disabled={isClassifying}>
            {isClassifying ? "Classifying..." : "Classify Pollution"}
          </Button>

          <SecondaryButton
            onClick={handleSave}
            disabled={isSaving || !pollutionLabel}
          >
            {isSaving ? "Saving..." : "Save to Firebase"}
          </SecondaryButton>
        </InputGroup>

        {pollutionLabel && (
          <ResultBox>
            <strong>Predicted: {pollutionLabel}</strong>
            <span>{new Date().toLocaleString()}</span>
          </ResultBox>
        )}
      </Card>

      <NotificationManager
        notifications={notifications}
        onRemove={removeNotification}
      />
    </DashboardContainer>
  );
}

export default Dashboard;
