/* Dashboard component */
function Dashboard() {
  const [sensorData, setSensorData] = useState({
    temp: 0,
    tds: 0,
    do: 0,
    ntu: 0,
    pH: 7.0
  });
  const [source, setSource] = useState('');
  const [error, setError] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const [isLoading, setIsLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);
  const [pollutionLabel, setPollutionLabel] = useState('');
  const [pollutionProbs, setPollutionProbs] = useState({});
  const [isClassifying, setIsClassifying] = useState(false);

  const addNotification = (message, type = 'success') => {
    const newNotification = { message, type };
    setNotifications(prev => [...prev, newNotification]);
  };
  const removeNotification = (index) => setNotifications(prev => prev.filter((_, i) => i !== index));

  useEffect(() => {
    let isMounted = true;
    const fetchData = async () => {
      try {
        const hostname = window.location.hostname;
        const response = await fetch(`http://${hostname}:5000/api/data`);
        if (!response.ok) throw new Error(`HTTP ${response.status}`);
        const data = await response.json();

        if (isMounted) {
          // parse numeric values and keep pH if provided else keep previous
          setSensorData(prev => ({
            temp: parseFloat(data.temp ?? prev.temp) || 0,
            tds: parseFloat(data.tds ?? prev.tds) || 0,
            do: parseFloat(data.do ?? prev.do) || 0,
            ntu: parseFloat(data.ntu ?? prev.ntu) || 0,
            pH: data.pH !== undefined ? parseFloat(data.pH) : prev.pH
          }));
          setError('');
          setConnectionStatus('Connected to ESP32');
          setIsLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError('Failed to fetch sensor data. Please check ESP32 connection.');
          setConnectionStatus('Connection Error');
          setIsLoading(false);
          console.error('Error fetching data:', err);
        }
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);
    return () => { isMounted = false; clearInterval(interval); };
  }, []);

  // -------------------------
  // Pollutant classification
  // Tries API first; falls back to local JS classifier
  // -------------------------
  const predictViaApi = async (payload) => {
    const host = process.env.REACT_APP_PREDICT_HOST || window.location.hostname;
    const port = process.env.REACT_APP_PREDICT_PORT || '6000';
    const url = `http://${host}:${port}/predict`;
    const res = await fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    });
    if (!res.ok) throw new Error('Prediction API returned error');
    return res.json();
  };

  // Local JS classifier that mirrors your Python rules
  const classifyLocal = (payload) => {
    const TDS = Number(payload.tds);
    const pH = Number(payload.pH);
    const turb = Number(payload.ntu);
    const DO = Number(payload.do);
    const temp = Number(payload.temp);

    // Good Water
    if (DO > 7 && TDS < 300 && turb < 5 && pH >= 6.5 && pH <= 8.5) return { label: 'Good Water', probs: {} };

    // Sewage
    if (DO < 3 && turb > 10) return { label: 'Sewage', probs: {} };

    // Agricultural Runoff
    if (TDS > 500 && turb > 10 && pH > 7.5) return { label: 'Agricultural Runoff', probs: {} };

    // Chemical Intrusion
    if (TDS > 1000 || pH < 6 || pH > 9) return { label: 'Chemical Intrusion', probs: {} };

    // Thermal Pollution
    if (temp > 30 && DO < 6) return { label: 'Thermal Pollution', probs: {} };

    return { label: 'Good Water', probs: {} };
  };

  const handleClassify = async () => {
    setIsClassifying(true);
    setPollutionLabel('');
    setPollutionProbs({});
    const payload = {
      temp: Number(sensorData.temp),
      tds: Number(sensorData.tds),
      do: Number(sensorData.do),
      ntu: Number(sensorData.ntu),
      pH: Number(sensorData.pH)
    };

    // Try API call first
    try {
      const result = await predictViaApi(payload);
      setPollutionLabel(result.label || '');
      setPollutionProbs(result.probabilities || {});
      addNotification('Classification successful', 'success');
    } catch (err) {
      // fallback to local classifier
      console.warn('Prediction API failed, using local classifier', err);
      const result = classifyLocal(payload);
      setPollutionLabel(result.label);
      setPollutionProbs(result.probs || {});
      addNotification('Classification completed locally', 'info');
    } finally {
      setIsClassifying(false);
    }
  };

  const handleSave = async () => {
    if (!source.trim()) {
      addNotification('Enter a water source name before saving', 'error');
      return;
    }
    if (!pollutionLabel) {
      addNotification('Please classify pollution before saving', 'error');
      return;
    }
    setIsSaving(true);
    try {
      const readingsRef = ref(database, 'readings');
      const newReadingRef = push(readingsRef);
      await set(newReadingRef, {
        temperature: Number(sensorData.temp),
        tds: Number(sensorData.tds),
        turbidity_quadratic: Number(sensorData.ntu),
        dissolvedOxygen: Number(sensorData.do),
        pH: Number(sensorData.pH),
        pollutionLabel,
        pollutionProbs: pollutionProbs || {},
        source: source.trim(),
        timestamp: new Date().toISOString()
      });
      addNotification('Saved reading to Firebase', 'success');
      setSource('');
    } catch (err) {
      console.error('Save error', err);
      addNotification('Failed to save reading', 'error');
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return (
      <DashboardContainer>
        <Card>
          <CardHeader>
            <Title>Loading Sensor Data...</Title>
          </CardHeader>
        </Card>
      </DashboardContainer>
    );
  }

  return (
    <DashboardContainer>
      <Card>
        <CardHeader>
          <Title>Current Sensor Readings</Title>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            <StatusBadge connected={connectionStatus === 'Connected to ESP32'}>
              {connectionStatus}
            </StatusBadge>
            {pollutionLabel && (
              <div style={{
                background: '#2563eb',
                color: 'white',
                padding: '8px 12px',
                borderRadius: 8,
                fontWeight: 600
              }}>
                {pollutionLabel}
              </div>
            )}
          </div>
        </CardHeader>

        {error && <div style={{ color: '#dc2626', marginBottom: '1rem' }}>{error}</div>}

        <DataGrid>
          <DataItem>
            <Label>Temperature</Label>
            <Gauge
              value={Number(sensorData.temp)}
              min={-10}
              max={50}
              unit="Â°C"
              warningThreshold={60}   /* gauge expects percentage thresholds; these will map by percentage calculation inside Gauge */
              dangerThreshold={85}
            />
          </DataItem>

          <DataItem>
            <Label>TDS</Label>
            <Value>{Number(sensorData.tds).toFixed(2)} ppm</Value>
          </DataItem>

          <DataItem>
            <Label>Turbidity (Quadratic)</Label>
            <Value>{Number(sensorData.ntu).toFixed(2)} NTU</Value>
          </DataItem>

          <DataItem>
            <Label>Dissolved Oxygen</Label>
            <Value>{Number(sensorData.do).toFixed(2)} mg/L</Value>
          </DataItem>

          <DataItem>
            <Label>pH</Label>
            <Value>
              <div style={{ display:'flex', alignItems:'center', gap: '8px', justifyContent: 'center' }}>
                <span>{Number(sensorData.pH).toFixed(2)}</span>
                <input
                  type="number"
                  step="0.01"
                  value={sensorData.pH}
                  onChange={(e) => setSensorData(s => ({ ...s, pH: e.target.value }))}
                  style={{ width: 80, padding: '6px', borderRadius: 6, border: '1px solid rgba(0,0,0,0.08)' }}
                />
              </div>
            </Value>
          </DataItem>
        </DataGrid>

        <InputGroup>
          <Input
            type="text"
            placeholder="Enter water source (e.g., tap, lake, well)"
            value={source}
            onChange={(e) => setSource(e.target.value)}
          />

          <Button onClick={handleClassify} disabled={isClassifying}>
            {isClassifying ? 'Classifying...' : 'Classify Pollution'}
          </Button>

          <SecondaryButton onClick={handleSave} disabled={isSaving || !pollutionLabel}>
            {isSaving ? 'Saving...' : 'Save to Firebase'}
          </SecondaryButton>
        </InputGroup>

        {pollutionLabel && (
          <ResultBox>
            <div>
              <strong>Predicted:</strong> &nbsp; <span style={{ fontSize: '1.1rem' }}>{pollutionLabel}</span>
              {pollutionProbs && Object.keys(pollutionProbs).length > 0 && (
                <div style={{ marginTop: 8 }}>
                  <small>Probabilities:</small>
                  <div style={{ display: 'flex', gap: 12, marginTop: 6, flexWrap: 'wrap' }}>
                    {Object.entries(pollutionProbs).map(([k, v]) => (
                      <div key={k} style={{ background: '#fff', padding: '6px 8px', borderRadius: 6, border: '1px solid #e6eefc' }}>
                        <small>{k}: {(v * 100).toFixed(1)}%</small>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            <div>
              <small>Timestamp: {new Date().toLocaleString()}</small>
            </div>
          </ResultBox>
        )}
      </Card>

      <NotificationManager notifications={notifications} onRemove={removeNotification} />
    </DashboardContainer>
  );
}

export default Dashboard;
