// src/components/MetricCard.js
import React from 'react';
import styled from 'styled-components';

/*
 Props:
  - title (string)
  - value (number/string)
  - unit (string)
  - sparkline (array of numbers)
  - statusText (string)
  - statusColor (string)
*/

const Card = styled.div`
  background: rgba(255,255,255,0.03);
  border-radius: 12px;
  padding: 14px;
  min-height: 110px;
  box-shadow: 0 8px 20px rgba(2,6,23,0.28);
  border: 1px solid rgba(255,255,255,0.02);
  display:flex;
  gap:14px;
  align-items:center;
  justify-content:space-between;
`;

const Left = styled.div`
  display:flex;
  flex-direction:column;
  gap:6px;
`;

const Title = styled.div`
  font-weight:700;
  color: ${({theme}) => theme.colors.text.secondary};
  font-size: 0.9rem;
`;

const Big = styled.div`
  font-weight:900;
  color: ${({theme}) => theme.colors.text.primary};
  font-size: 1.6rem;
`;

const Right = styled.div`
  width: 120px;
  text-align:right;
`;

const Status = styled.div`
  color: ${p => p.color || '#10b981'};
  font-weight:700;
  font-size: 0.9rem;
`;

export default function MetricCard({ title, value, unit='', sparkline = [], statusText = '', statusColor }) {
  // normalize sparkline to width/height
  const points = (sparkline && sparkline.length) ? sparkline : [0,0,0,0,0,0];
  const w = 120, h = 40;
  const max = Math.max(...points), min = Math.min(...points);
  const norm = (v) => (max - min === 0) ? h/2 : h - ((v - min) / (max - min)) * h;
  const path = points.map((v, i) => `${i === 0 ? 'M' : 'L'} ${ (i/(points.length-1))*w } ${ norm(v) }`).join(' ');

  return (
    <Card>
      <Left>
        <Title>{title}</Title>
        <Big>{Number(value).toFixed( (typeof value === 'number' && Math.abs(value) < 10) ? 1 : 0 )} {unit}</Big>
        <div style={{ fontSize: 12, color: 'rgba(255,255,255,0.7)' }}>{statusText}</div>
      </Left>

      <Right>
        <svg width={w} height={h} viewBox={`0 0 ${w} ${h}`} preserveAspectRatio="none">
          <path d={path} fill="rgba(99,102,241,0.08)" stroke="rgba(99,102,241,0.92)" strokeWidth="2" strokeLinejoin="round" strokeLinecap="round" />
        </svg>
        <div style={{ height:6 }} />
        <Status color={statusColor}>{statusText}</Status>
      </Right>
    </Card>
  );
}
