// src/pages/About.js
import React, { useState } from 'react';
import styled from 'styled-components';

const PageWrap = styled.div`
  padding: 40px 20px 60px;
  max-width: 1100px;
  margin: 0 auto;
`;

const HeadingBlock = styled.div`
  text-align: center;
  margin-bottom: 28px;
`;

const Title = styled.h1`
  font-size: 2.1rem;
  font-weight: 700;
  margin-bottom: 12px;
  color: ${({ theme }) => theme.colors.text.primary};
`;

const Subtitle = styled.p`
  max-width: 720px;
  margin: 0 auto;
  font-size: 0.98rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

/* Tabs row */
const TabsRow = styled.div`
  display: inline-flex;
  margin-top: 20px;
  padding: 4px;
  border-radius: 999px;
  background: rgba(15,23,42,0.76);
  box-shadow:
    0 18px 40px rgba(15,23,42,0.9),
    inset 0 1px 0 rgba(255,255,255,0.05);
  border: 1px solid rgba(148,163,184,0.5);
`;

const TabButton = styled.button`
  border: none;
  outline: none;
  padding: 9px 22px;
  border-radius: 999px;
  font-size: 0.9rem;
  font-weight: 600;
  cursor: pointer;
  background: ${({ active }) =>
    active ? 'linear-gradient(180deg,#3b82f6,#2563eb)' : 'transparent'};
  color: ${({ active }) => (active ? '#ffffff' : 'rgba(226,232,240,0.9)')};
  box-shadow: ${({ active }) =>
    active ? '0 14px 36px rgba(37,99,235,0.55)' : 'none'};
`;

/* Big glass ‚Äúslab‚Äù container ‚Äì matches History rows style */
const GlassSection = styled.section`
  margin-top: 26px;
  padding: 26px 26px 24px;
  border-radius: 22px;
  background: rgba(15,23,42,0.88);
  backdrop-filter: blur(14px) saturate(120%);
  -webkit-backdrop-filter: blur(14px) saturate(120%);
  border: 1px solid rgba(148,163,184,0.35);
  box-shadow:
    0 26px 70px rgba(15,23,42,0.95),
    inset 0 1px 0 rgba(255,255,255,0.03);
  color: ${({ theme }) => theme.colors.text.primary};
`;

const SectionTitleRow = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 12px;
`;

const IconBubble = styled.div`
  width: 32px;
  height: 32px;
  border-radius: 999px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 18px;
  background: rgba(59,130,246,0.14);
  border: 1px solid rgba(96,165,250,0.6);
  color: #bfdbfe;
`;

const SectionTitle = styled.h2`
  font-size: 1.1rem;
  font-weight: 700;
  margin: 0;
`;

const Paragraph = styled.p`
  font-size: 0.95rem;
  line-height: 1.7;
  color: ${({ theme }) => theme.colors.text.secondary};
  margin-bottom: 14px;
`;

const BulletList = styled.ul`
  margin: 6px 0 0 1.1rem;
  padding: 0;
  font-size: 0.94rem;
  color: ${({ theme }) => theme.colors.text.secondary};
  line-height: 1.7;
`;

const Bullet = styled.li`
  margin-bottom: 4px;
`;

/* Split columns inside glass section */
const TwoCol = styled.div`
  display: grid;
  grid-template-columns: minmax(0, 1.1fr) minmax(0, 1fr);
  gap: 18px;
  margin-top: 12px;

  @media (max-width: 900px) {
    grid-template-columns: 1fr;
  }
`;

const MetaCard = styled.div`
  border-radius: 16px;
  padding: 14px 16px;
  background: rgba(15,23,42,0.9);
  border: 1px solid rgba(148,163,184,0.5);
  box-shadow: 0 16px 40px rgba(15,23,42,0.9);
  font-size: 0.9rem;
`;

const MetaTitle = styled.div`
  font-weight: 600;
  margin-bottom: 6px;
  color: ${({ theme }) => theme.colors.text.primary};
`;

const MetaRow = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 4px;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

const Badge = styled.span`
  padding: 3px 10px;
  border-radius: 999px;
  font-size: 0.78rem;
  font-weight: 600;
  background: ${({ tone }) =>
    tone === 'good'
      ? 'linear-gradient(180deg,#22c55e,#16a34a)'
      : 'linear-gradient(180deg,#38bdf8,#0ea5e9)'};
  color: #f9fafb;
`;

/* Team cards */
const TeamGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit,minmax(220px,1fr));
  gap: 16px;
  margin-top: 10px;
`;

const TeamCard = styled.div`
  border-radius: 18px;
  padding: 16px 18px 14px;
  background: rgba(15,23,42,0.92);
  border: 1px solid rgba(148,163,184,0.6);
  box-shadow: 0 18px 46px rgba(15,23,42,0.98);
`;

const TeamName = styled.div`
  font-weight: 700;
  margin-bottom: 4px;
`;

const TeamRole = styled.div`
  font-size: 0.86rem;
  color: ${({ theme }) => theme.colors.text.secondary};
  margin-bottom: 8px;
`;

const TeamDetail = styled.div`
  font-size: 0.82rem;
  color: ${({ theme }) => theme.colors.text.muted};
`;

const About = () => {
  const [tab, setTab] = useState<'overview' | 'specs' | 'team'>('overview');

  return (
    <PageWrap>
      <HeadingBlock>
        <Title>About Smart Water Quality Monitoring System</Title>
        <Subtitle>
          Droplet is an ESP32-based IoT system that measures key water quality
          parameters in real time and helps classify pollution level so that
          action can be taken early.
        </Subtitle>

        <TabsRow>
          <TabButton active={tab === 'overview'} onClick={() => setTab('overview')}>
            Overview
          </TabButton>
          <TabButton active={tab === 'specs'} onClick={() => setTab('specs')}>
            Specifications
          </TabButton>
          <TabButton active={tab === 'team'} onClick={() => setTab('team')}>
            Our Team
          </TabButton>
        </TabsRow>
      </HeadingBlock>

      {/* ================= OVERVIEW ================= */}
      {tab === 'overview' && (
        <>
          <GlassSection>
            <SectionTitleRow>
              <IconBubble>üìä</IconBubble>
              <SectionTitle>System Overview</SectionTitle>
            </SectionTitleRow>

            <Paragraph>
              This project is a real-time IoT-based water quality monitoring
              system designed around the ESP32 microcontroller. It continuously
              measures four critical parameters that indicate overall water
              health:
            </Paragraph>

            <BulletList>
              <Bullet>Temperature (via DS18B20 waterproof digital sensor)</Bullet>
              <Bullet>TDS ‚Äì Total Dissolved Solids (ppm)</Bullet>
              <Bullet>Dissolved Oxygen (DO) in mg/L</Bullet>
              <Bullet>Turbidity (NTU ‚Äì cloudiness of the water)</Bullet>
            </BulletList>

            <Paragraph>
              The processed data is displayed on this dashboard and can be stored
              in Firebase when the user chooses, creating a clean history of only
              the readings that matter.
            </Paragraph>

            <TwoCol>
              <div>
                <MetaTitle>Why this matters</MetaTitle>
                <BulletList>
                  <Bullet>
                    Detects contamination trends in lakes, tanks, and tap water.
                  </Bullet>
                  <Bullet>
                    Helps classify water as <strong>Good</strong> or polluted
                    (e.g., sewage, chemical intrusion, agricultural runoff).
                  </Bullet>
                  <Bullet>
                    Provides a simple, student-friendly showcase of IoT + sensors
                    + cloud + UI.
                  </Bullet>
                </BulletList>
              </div>

              <MetaCard>
                <MetaTitle>System Summary</MetaTitle>
                <MetaRow>
                  <span>Microcontroller</span>
                  <span>ESP32 (DOIT DevKit V1)</span>
                </MetaRow>
                <MetaRow>
                  <span>Connectivity</span>
                  <span>Wi-Fi (local network)</span>
                </MetaRow>
                <MetaRow>
                  <span>Cloud Storage</span>
                  <span>Firebase Realtime DB</span>
                </MetaRow>
                <MetaRow>
                  <span>Dashboard</span>
                  <span>React + styled-components</span>
                </MetaRow>
                <MetaRow>
                  <span>Status</span>
                  <Badge tone="good">Working Prototype</Badge>
                </MetaRow>
              </MetaCard>
            </TwoCol>
          </GlassSection>

          <GlassSection>
            <SectionTitleRow>
              <IconBubble>üß™</IconBubble>
              <SectionTitle>Sensor Integration</SectionTitle>
            </SectionTitleRow>

            <Paragraph>
              Each sensor is calibrated and read through the ESP32&apos;s ADC or
              One-Wire interface. The raw values are converted into engineering
              units on the microcontroller before being sent to the dashboard.
            </Paragraph>

            <BulletList>
              <Bullet>
                <strong>DS18B20</strong> ‚Äì digital temperature sensor using the
                One-Wire protocol for accurate water temperature readings.
              </Bullet>
              <Bullet>
                <strong>TDS sensor</strong> ‚Äì measures conductivity and converts
                it to TDS (ppm) to estimate dissolved solids such as salts and
                minerals.
              </Bullet>
              <Bullet>
                <strong>DO sensor</strong> ‚Äì measures the amount of dissolved
                oxygen, which is a key indicator of aquatic life health.
              </Bullet>
              <Bullet>
                <strong>Turbidity sensor</strong> ‚Äì detects suspended particles
                in water using a calibrated non-linear (quadratic) model.
              </Bullet>
            </BulletList>
          </GlassSection>
        </>
      )}

      {/* ================= SPECIFICATIONS ================= */}
      {tab === 'specs' && (
        <>
          <GlassSection>
            <SectionTitleRow>
              <IconBubble>‚öôÔ∏è</IconBubble>
              <SectionTitle>Hardware Specifications</SectionTitle>
            </SectionTitleRow>

            <TwoCol>
              <div>
                <BulletList>
                  <Bullet>ESP32 DOIT DevKit V1, dual-core 240&nbsp;MHz</Bullet>
                  <Bullet>Operating Voltage: 3.3&nbsp;V</Bullet>
                  <Bullet>Wi-Fi 802.11 b/g/n, 2.4&nbsp;GHz</Bullet>
                  <Bullet>DS18B20 digital temperature sensor</Bullet>
                  <Bullet>TDS analog sensor (0‚Äì1000+ ppm range)</Bullet>
                  <Bullet>Dissolved Oxygen sensor with calibration support</Bullet>
                  <Bullet>Turbidity sensor (NTU output, 0‚Äì3000 NTU typical)</Bullet>
                </BulletList>
              </div>

              <MetaCard>
                <MetaTitle>Software Stack</MetaTitle>
                <BulletList>
                  <Bullet>ESP32 Arduino core (C/C++)</Bullet>
                  <Bullet>DallasTemperature + OneWire libraries</Bullet>
                  <Bullet>Custom calibration logic for DO &amp; turbidity</Bullet>
                  <Bullet>Firebase Realtime Database (push-only by user)</Bullet>
                  <Bullet>React SPA dashboard with live polling from ESP32</Bullet>
                </BulletList>
              </MetaCard>
            </TwoCol>
          </GlassSection>

          <GlassSection>
            <SectionTitleRow>
              <IconBubble>üì°</IconBubble>
              <SectionTitle>Operating Modes</SectionTitle>
            </SectionTitleRow>

            <BulletList>
              <Bullet>
                <strong>Live Mode:</strong> ESP32 streams current sensor values
                over local Wi-Fi; the React dashboard polls the `/data` endpoint
                and updates in real time.
              </Bullet>
              <Bullet>
                <strong>Save on Demand:</strong> Readings are only written to
                Firebase when the user clicks <em>Save to Firebase</em>, keeping
                history clean and intentional.
              </Bullet>
              <Bullet>
                <strong>Manual Test Mode:</strong> A dedicated screen allows
                manual entry of parameters to test the classifier without
                hardware.
              </Bullet>
            </BulletList>
          </GlassSection>
        </>
      )}

      {/* ================= TEAM ================= */}
      {tab === 'team' && (
        <GlassSection>
          <SectionTitleRow>
            <IconBubble>üë•</IconBubble>
            <SectionTitle>Project Team</SectionTitle>
          </SectionTitleRow>

          <Paragraph>
            This project was designed, implemented, and tested as part of an
            academic IoT / embedded systems course. Roles can be adapted to match
            your actual team.
          </Paragraph>

          <TeamGrid>
            <TeamCard>
              <TeamName>Your Name</TeamName>
              <TeamRole>Embedded &amp; Sensor Integration</TeamRole>
              <TeamDetail>
                Wiring sensors, calibration, ESP32 firmware, and serial
                debugging.
              </TeamDetail>
            </TeamCard>

            <TeamCard>
              <TeamName>Teammate 2</TeamName>
              <TeamRole>Web Dashboard &amp; Firebase</TeamRole>
              <TeamDetail>
                React UI, Firebase connection, history view, and pollution
                classifier.
              </TeamDetail>
            </TeamCard>

            <TeamCard>
              <TeamName>Teammate 3</TeamName>
              <TeamRole>Documentation &amp; Testing</TeamRole>
              <TeamDetail>
                Flow diagrams, system description, test cases, and report
                formatting.
              </TeamDetail>
            </TeamCard>
          </TeamGrid>
        </GlassSection>
      )}
    </PageWrap>
  );
};

export default About;
