// src/pages/About.js
import React, { useState } from 'react';
import styled from 'styled-components';
import SystemInfo from '../components/SystemInfo';
import Specs from '../components/Specs';
import Team from '../components/Team';

/* ---------- PAGE WRAPPER ---------- */
const Container = styled.div`
  padding: 40px 20px 60px;
  max-width: 1100px;
  margin: 0 auto;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

/* ---------- ✅ GLASS HEADER BANNER ---------- */
const GlassHeader = styled.div`
  width: 100%;
  max-width: 1000px;
  margin-bottom: 36px;
  padding: 26px 30px;
  border-radius: 22px;

  background: rgba(15, 23, 42, 0.88);
  backdrop-filter: blur(16px) saturate(120%);
  -webkit-backdrop-filter: blur(16px) saturate(120%);
  border: 1px solid rgba(148, 163, 184, 0.45);

  box-shadow:
    0 30px 80px rgba(15, 23, 42, 0.98),
    inset 0 1px 0 rgba(255, 255, 255, 0.04);

  text-align: center;
`;

/* ---------- ✅ TITLE (NO HOVER, HIGH CONTRAST) ---------- */
const Title = styled.h1`
  font-size: 2.25rem;
  font-weight: 800;
  margin: 0 0 10px;
  color: #f8fafc;  /* ✅ bright white */

  /* ✅ kill all hover effects */
  pointer-events: none;

  @media (max-width: 768px) {
    font-size: 1.8rem;
  }
`;

/* ---------- ✅ SUBTITLE (SOFT BUT READABLE) ---------- */
const Subtitle = styled.p`
  margin: 0;
  font-size: 0.95rem;
  line-height: 1.6;
  color: rgba(226, 232, 240, 0.95);  /* ✅ light gray */

  pointer-events: none;
`;

/* ---------- TABS ---------- */
const TabsOuter = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  margin-bottom: 28px;
`;

const TabContainer = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 4px;
  padding: 6px;
  border-radius: 999px;
  background: rgba(15, 23, 42, 0.85);
  border: 1px solid rgba(148, 163, 184, 0.45);

  box-shadow:
    0 20px 50px rgba(15, 23, 42, 0.95),
    inset 0 1px 0 rgba(255, 255, 255, 0.03);
`;

const Tab = styled.button`
  padding: 10px 20px;
  border-radius: 999px;
  border: none;
  outline: none;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 700;

  background: ${props =>
    props.active
      ? 'linear-gradient(180deg,#3b82f6,#2563eb)'
      : 'transparent'};

  color: ${props =>
    props.active ? '#ffffff' : 'rgba(226,232,240,0.9)'};

  box-shadow: ${props =>
    props.active
      ? '0 16px 36px rgba(37,99,235,0.6)'
      : 'none'};

  /* ✅ REMOVE HOVER COMPLETELY */
  &:hover {
    background: ${props =>
      props.active
        ? 'linear-gradient(180deg,#3b82f6,#2563eb)'
        : 'transparent'};
    box-shadow: ${props =>
      props.active
        ? '0 16px 36px rgba(37,99,235,0.6)'
        : 'none'};
    transform: none;
  }
`;

/* ---------- ✅ GLASS CONTENT SLAB ---------- */
const ContentContainer = styled.div`
  width: 100%;
  max-width: 1000px;
  padding: 26px;
  border-radius: 22px;

  background: rgba(15, 23, 42, 0.9);
  backdrop-filter: blur(14px) saturate(120%);
  -webkit-backdrop-filter: blur(14px) saturate(120%);
  border: 1px solid rgba(148, 163, 184, 0.4);

  box-shadow:
    0 26px 70px rgba(15, 23, 42, 0.98),
    inset 0 1px 0 rgba(255, 255, 255, 0.03);
`;

/* ---------- COMPONENT ---------- */
const About = () => {
  const [activeTab, setActiveTab] = useState('overview');

  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return <SystemInfo />;
      case 'specs':
        return <Specs />;
      case 'team':
        return <Team />;
      default:
        return <SystemInfo />;
    }
  };

  return (
    <Container>

      {/* ✅✅✅ FIXED GLASS HEADER */}
      <GlassHeader>
        <Title>About Smart Water Quality Monitoring System</Title>
        <Subtitle>
          A real-time ESP32-based IoT project that measures water quality
          parameters, helps classify pollution, and stores only the readings
          you choose in Firebase.
        </Subtitle>
      </GlassHeader>

      <TabsOuter>
        <TabContainer>
          <Tab active={activeTab === 'overview'} onClick={() => setActiveTab('overview')}>
            Overview
          </Tab>
          <Tab active={activeTab === 'specs'} onClick={() => setActiveTab('specs')}>
            Specifications
          </Tab>
          <Tab active={activeTab === 'team'} onClick={() => setActiveTab('team')}>
            Our Team
          </Tab>
        </TabContainer>
      </TabsOuter>

      <ContentContainer>
        {renderContent()}
      </ContentContainer>

    </Container>
  );
};

export default About;
