// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import { ref, push, set } from 'firebase/database';
import { database } from '../firebase';
import styled from 'styled-components';

// If you have a Navbar component, it will still render (App routes show only page content).
const Page = styled.div`
  padding: 28px;
  color: #222;
`;

/* Simple card styles; keeps glass feel minimal */
const Card = styled.div`
  background: rgba(255,255,255,0.04);
  border-radius: 12px;
  padding: 18px;
  margin-bottom: 12px;
  backdrop-filter: blur(4px);
  border: 1px solid rgba(0,0,0,0.05);
`;

const Row = styled.div`
  display:flex;
  gap:12px;
  align-items:center;
  flex-wrap:wrap;
`;

const BigValue = styled.div`
  font-size: 28px;
  font-weight: 800;
`;

/* actions */
const Input = styled.input`
  padding:10px 12px;
  border-radius:10px;
  border:1px solid rgba(0,0,0,0.08);
  flex:1;
`;

const Button = styled.button`
  padding:10px 14px;
  background: #2563eb;
  color: #fff;
  border:none;
  border-radius:10px;
  font-weight:700;
  cursor:pointer;
  margin-left:8px;
`;

export default function Dashboard() {
  const [sensor, setSensor] = useState({ temperature: 0, tds: 0, turbidity: 0, dissolvedOxygen: 0, pH: 7 });
  const [connected, setConnected] = useState(false);
  const [source, setSource] = useState('');
  const [label, setLabel] = useState('');

  useEffect(() => {
    let mounted = true;
    async function fetcher() {
      try {
        const host = window.location.hostname;
        const res = await fetch(`http://${host}:5000/api/data`);
        if (!res.ok) throw new Error('no data');
        const data = await res.json();
        if (!mounted) return;
        setSensor(prev => ({
          temperature: Number(data.temp ?? prev.temperature),
          tds: Number(data.tds ?? prev.tds),
          turbidity: Number(data.ntu ?? prev.turbidity),
          dissolvedOxygen: Number(data.do ?? prev.dissolvedOxygen),
          pH: Number(data.pH ?? prev.pH)
        }));
        setConnected(true);
      } catch (e) {
        setConnected(false);
      }
    }

    fetcher();
    const id = setInterval(fetcher, 2500);
    return () => { mounted=false; clearInterval(id); };
  }, []);

  const classify = (s) => {
    const { tds, pH, turbidity: ntu, dissolvedOxygen: DO, temperature } = s;
    if (DO > 7 && tds < 300 && ntu < 5 && pH >= 6.5 && pH <= 8.5) return "Good Water";
    if (DO < 3 && ntu > 10) return "Sewage";
    if (tds > 500 && ntu > 10 && pH > 7.5) return "Agricultural Runoff";
    if (tds > 1000 || pH < 6 || pH > 9) return "Chemical Intrusion";
    if (temperature > 30 && DO < 6) return "Thermal Pollution";
    return "Good Water";
  };

  const handleClassify = () => {
    const l = classify(sensor);
    setLabel(l);
  };

  const handleSave = async () => {
    if (!label) return alert('Classify first');
    if (!source.trim()) return alert('Enter water source');
    const r = ref(database, 'readings');
    const n = push(r);
    await set(n, {
      temperature: Number(sensor.temperature),
      tds: Number(sensor.tds),
      turbidity: Number(sensor.turbidity),
      dissolvedOxygen: Number(sensor.dissolvedOxygen),
      pH: Number(sensor.pH),
      pollutionLabel: label,
      source,
      timestamp: new Date().toISOString()
    });
    setSource('');
    alert('Saved');
  };

  return (
    <Page>
      <h1>Current Sensor Readings</h1>

      <Card>
        <Row>
          <div style={{ flex:1 }}>
            <div style={{ fontWeight:700 }}>Temperature</div>
            <BigValue>{Number(sensor.temperature).toFixed(2)} Â°C</BigValue>
          </div>

          <div style={{ flex:1 }}>
            <div style={{ fontWeight:700 }}>TDS</div>
            <BigValue>{Number(sensor.tds).toFixed(2)} ppm</BigValue>
          </div>

          <div style={{ flex:1 }}>
            <div style={{ fontWeight:700 }}>Turbidity</div>
            <BigValue>{Number(sensor.turbidity).toFixed(2)} NTU</BigValue>
          </div>

          <div style={{ flex:1 }}>
            <div style={{ fontWeight:700 }}>Dissolved O2</div>
            <BigValue>{Number(sensor.dissolvedOxygen).toFixed(2)} mg/L</BigValue>
          </div>

          <div style={{ flex:1 }}>
            <div style={{ fontWeight:700 }}>pH (read-only)</div>
            <BigValue>{Number(sensor.pH).toFixed(2)}</BigValue>
          </div>
        </Row>
      </Card>

      <Card>
        <h3>Actions</h3>
        <Row>
          <Input placeholder="Enter water source (e.g., tap, lake, well)" value={source} onChange={e => setSource(e.target.value)} />
          <div style={{ display:'flex', gap:8 }}>
            <Button onClick={handleClassify}>Classify Pollution</Button>
            <Button style={{ background:'#10b981' }} onClick={handleSave}>Save to Firebase</Button>
          </div>
        </Row>

        {label && <div style={{ marginTop:12, padding:10, background:'rgba(255,255,255,0.03)', borderRadius:8 }}>Predicted: <strong>{label}</strong></div>}
      </Card>

      <div style={{ color: connected ? '#10b981' : '#ef4444', fontWeight:800 }}>
        {connected ? 'Connected to ESP32' : 'Connection Error: failed to fetch sensor data'}
      </div>
    </Page>
  );
}
