// src/pages/Dashboard.js
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import MetricCard from "../components/MetricCard";
import Alerts from "../components/Alerts";
import Gauge from "../components/Gauge";
import { ref, push, set } from "firebase/database";
import { database } from "../firebase";
import { useNavigate } from "react-router-dom";

/* ---- Layout ---- */
const Page = styled.div`
  padding: 22px 28px;
  color: #fff;
`;

const HeaderRow = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom: 18px;
`;

const Title = styled.h1`
  margin:0;
  font-size: 28px;
  color: #ffffff;
  letter-spacing: -0.5px;
`;

const ConnBadge = styled.div`
  padding: 8px 14px;
  border-radius: 999px;
  font-weight:800;
  background: ${p => p.connected ? 'rgba(16,185,129,0.12)' : 'rgba(239,68,68,0.10)'};
  color: ${p => p.connected ? '#064e3b' : '#7f1d1d'};
  border: 1px solid ${p => p.connected ? 'rgba(16,185,129,0.22)' : 'rgba(239,68,68,0.22)'};
`;

/* Top metrics grid (4 across at wide screens) */
const TopGrid = styled.div`
  display:grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 14px;
  margin-bottom: 18px;
  @media (max-width:1200px) { grid-template-columns: repeat(2, 1fr); }
  @media (max-width:600px) { grid-template-columns: 1fr; }
`;

/* Main region two-column */
const MainGrid = styled.div`
  display:grid;
  grid-template-columns: 2fr 1fr;
  gap: 18px;
  @media (max-width:1000px) { grid-template-columns: 1fr; }
`;

const Fog = styled.div`
  background: rgba(255,255,255,0.06);
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 14px 40px rgba(2,6,23,0.30);
  border: 1px solid rgba(255,255,255,0.06);
  backdrop-filter: blur(6px);
  color: #fff;
`;

/* Small info tiles inside left column */
const InfoGrid = styled.div`
  display:grid;
  grid-template-columns: 1fr;
  gap: 14px;
`;

const BottomGrid = styled.div`
  display:grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 14px;
  margin-top: 18px;
  @media (max-width:900px) { grid-template-columns: 1fr; }
`;

/* Actions area */
const Actions = styled.div`
  display:flex;
  flex-direction:column;
  gap: 12px;
`;

const SourceInput = styled.input`
  padding: 12px 14px;
  border-radius: 12px;
  border: 1px solid rgba(0,0,0,0.06);
  background: #fff;
  color: #111;
  font-weight:700;
`;

const BtnRow = styled.div`
  display:flex;
  gap: 10px;
  justify-content:flex-end;
  align-items:center;
`;

const PrimaryBtn = styled.button`
  min-width:140px;
  padding: 10px 14px;
  border-radius: 12px;
  color:#fff;
  border:none;
  font-weight:800;
  cursor:pointer;
  background: linear-gradient(180deg,#3b82f6,#2563eb);
  box-shadow: 0 8px 20px rgba(37,99,235,0.12);
`;

const SaveBtn = styled(PrimaryBtn)`
  background: linear-gradient(180deg,#10b981,#059669);
  box-shadow: 0 8px 20px rgba(16,185,129,0.12);
`;

/* ---------- Component ---------- */
export default function Dashboard() {
  const navigate = useNavigate();
  const [sensorData, setSensorData] = useState({ temp: 0, tds: 0, ntu: 0, do: 0, pH: 7.0 });
  const [connected, setConnected] = useState(false);
  const [source, setSource] = useState("");
  const [label, setLabel] = useState("");

  useEffect(() => {
    let mounted = true;
    async function fetcher() {
      try {
        const host = window.location.hostname;
        const res = await fetch(`http://${host}:5000/api/data`);
        if (!res.ok) throw new Error("no");
        const j = await res.json();
        if (!mounted) return;
        setSensorData({
          temp: Number(j.temp ?? sensorData.temp),
          tds: Number(j.tds ?? sensorData.tds),
          ntu: Number(j.ntu ?? sensorData.ntu),
          do: Number(j.do ?? sensorData.do),
          pH: Number(j.pH ?? sensorData.pH),
        });
        setConnected(true);
      } catch (e) {
        setConnected(false);
      }
    }
    fetcher();
    const id = setInterval(fetcher, 2500);
    return () => { mounted = false; clearInterval(id); };
  }, []);

  const classifyLocal = ({ tds, pH, ntu, do: DO, temp }) => {
    if (DO > 7 && tds < 300 && ntu < 5 && pH >= 6.5 && pH <= 8.5) return "Good Water";
    if (DO < 3 && ntu > 10) return "Sewage";
    if (tds > 500 && ntu > 10 && pH > 7.5) return "Agricultural Runoff";
    if (tds > 1000 || pH < 6 || pH > 9) return "Chemical Intrusion";
    if (temp > 30 && DO < 6) return "Thermal Pollution";
    return "Good Water";
  };

  const handleClassify = () => {
    const l = classifyLocal(sensorData);
    setLabel(l);
  };

  const handleSave = async () => {
    if (!label) return alert("Classify first");
    if (!source.trim()) return alert("Enter water source");
    const r = ref(database, "readings");
    const n = push(r);
    await set(n, {
      temperature: Number(sensorData.temp),
      tds: Number(sensorData.tds),
      turbidity: Number(sensorData.ntu),
      dissolvedOxygen: Number(sensorData.do),
      pH: Number(sensorData.pH),
      pollutionLabel: label,
      source,
      timestamp: new Date().toISOString(),
    });
    setSource("");
    alert("Saved to Firebase");
  };

  const metricRows = [
    { title: "pH Level", value: sensorData.pH, unit: "pH", sparkline: [6.8,7.0,7.1,7.2,7.0,sensorData.pH], statusText: "Normal", statusColor: "#10b981" },
    { title: "Turbidity", value: sensorData.ntu, unit: "NTU", sparkline: [0.5,1.2,1.1,1.4,1.2,sensorData.ntu], statusText: "Stable", statusColor: "#60a5fa" },
    { title: "TDS", value: sensorData.tds, unit: "ppm", sparkline: [200,220,230,240,238,sensorData.tds], statusText: "Check Required", statusColor: "#f59e0b" },
    { title: "Temperature", value: sensorData.temp, unit: "°C", sparkline: [23,24,24.5,24.2,23.8,sensorData.temp], statusText: "Normal", statusColor: "#7c3aed" },
  ];

  return (
    <Page>
      <HeaderRow>
        <Title>Current Sensor Readings</Title>
        <ConnBadge connected={connected}>{connected ? "Connected to ESP32" : "⚠ Connection Error"}</ConnBadge>
      </HeaderRow>

      <TopGrid>
        {metricRows.map((m, i) => (
          <MetricCard key={i} {...m} />
        ))}
      </TopGrid>

      <MainGrid>
        {/* left: gauge + two info tiles */}
        <div>
          <Fog style={{ marginBottom: 14 }}>
            <div style={{ display: "flex", gap: 18, alignItems: "center" }}>
              <div style={{ width: 220 }}>
                <Gauge value={Number(sensorData.temp)} min={-10} max={50} unit="°C" />
              </div>

              <div style={{ flex: 1 }}>
                <h3 style={{ marginTop: 0, marginBottom: 6 }}>Temperature</h3>
                <div style={{ fontSize: 28, fontWeight: 900 }}>{Number(sensorData.temp).toFixed(2)} °C</div>
                <div style={{ marginTop: 8, color: "rgba(255,255,255,0.78)" }}>Live sensor reading</div>
              </div>
            </div>
          </Fog>

          <BottomGrid>
            <Fog>
              <h4 style={{ marginTop: 0 }}>TDS</h4>
              <div style={{ fontSize: 20, fontWeight:800 }}>{Number(sensorData.tds).toFixed(2)} ppm</div>
            </Fog>

            <Fog>
              <h4 style={{ marginTop: 0 }}>Turbidity (Quadratic)</h4>
              <div style={{ fontSize: 20, fontWeight:800 }}>{Number(sensorData.ntu).toFixed(2)} NTU</div>
            </Fog>
          </BottomGrid>
        </div>

        {/* right: alerts + actions */}
        <div style={{ display: "flex", flexDirection: "column", gap: 14 }}>
          <Alerts />

          <Fog>
            <h4 style={{ marginTop: 0 }}>Actions</h4>
            <Actions>
              <SourceInput placeholder="Enter water source (e.g., tap, lake, well)" value={source} onChange={e => setSource(e.target.value)} />
              <BtnRow>
                <PrimaryBtn onClick={handleClassify}>Classify Pollution</PrimaryBtn>
                <SaveBtn onClick={handleSave}>Save to Firebase</SaveBtn>
              </BtnRow>

              {label && (
                <div style={{ marginTop: 12, padding: 12, borderRadius: 10, background: "rgba(0,0,0,0.12)", fontWeight:800 }}>
                  Predicted: {label}
                </div>
              )}
            </Actions>
          </Fog>
        </div>
      </MainGrid>
    </Page>
  );
}
