// src/components/MetricCard.js
import React from "react";
import styled from "styled-components";

/*
Compact metric card with small sparkline.
Props:
 - title, value, unit, sparkline (array), statusText, statusColor
*/

const Card = styled.div`
  background: rgba(255,255,255,0.08);      /* lighter glass for contrast */
  border-radius: 12px;
  padding: 16px;
  min-height: 110px;
  box-shadow: 0 8px 20px rgba(2,6,23,0.35);
  border: 1px solid rgba(255,255,255,0.06);
  display:flex;
  align-items:center;
  justify-content:space-between;
  color: #ffffff;                          /* strong white text for readability */
  backdrop-filter: blur(6px);
`;

const Left = styled.div`
  display:flex;
  flex-direction:column;
  gap:6px;
  min-width: 60%;
`;

const Title = styled.div`
  font-weight:700;
  color: rgba(255,255,255,0.92);
  font-size: 0.92rem;
`;

const Big = styled.div`
  font-weight:900;
  color: #ffffff;
  font-size: 1.45rem;
  letter-spacing: -0.5px;
`;

const Right = styled.div`
  width: 120px;
  text-align:right;
  display:flex;
  flex-direction:column;
  align-items:flex-end;
  gap:8px;
`;

const Status = styled.div`
  color: ${p => p.color || "#10b981"};
  font-weight:800;
  font-size: 0.85rem;
`;

export default function MetricCard({ title, value = 0, unit = "", sparkline = [], statusText = "", statusColor = "#10b981" }) {
  // Sparkline path
  const pts = (sparkline && sparkline.length > 0) ? sparkline : [0,0,0,0,0,0];
  const w = 110, h = 36;
  const max = Math.max(...pts), min = Math.min(...pts);
  const norm = v => (max - min === 0) ? h/2 : h - ((v - min) / (max - min)) * h;
  const path = pts.map((v, i) => `${i===0 ? 'M' : 'L'} ${(i/(pts.length-1))*w} ${norm(v)}`).join(' ');

  // display formatting
  const displayValue = (typeof value === 'number') ? (Math.abs(value) < 10 ? value.toFixed(2) : value.toFixed(1)) : value;

  return (
    <Card>
      <Left>
        <Title>{title}</Title>
        <Big>{displayValue} {unit}</Big>
        <div style={{ fontSize: 12, color: 'rgba(255,255,255,0.78)' }}>{/* optional small text */}</div>
      </Left>

      <Right>
        <svg width={w} height={h} viewBox={`0 0 ${w} ${h}`} preserveAspectRatio="none">
          <path d={path}
                fill="rgba(99,102,241,0.06)"
                stroke="rgba(99,102,241,0.98)"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round" />
        </svg>
        <Status color={statusColor}>{statusText}</Status>
      </Right>
    </Card>
  );
}
