// src/globalStyles.js
import { createGlobalStyle } from 'styled-components';

/* Add Inter font in public/index.html if not present:
<link href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap" rel="stylesheet">
*/

export const GlobalStyle = createGlobalStyle`
  *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
  html, body, #root { height: 100%; }
  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Open Sans', sans-serif;
    line-height: 1.5;
    min-height: 100vh;
    margin: 0;
    padding: 0;
    color: ${({theme}) => theme.colors.text.primary};
    background: #071122;
    -webkit-font-smoothing:antialiased;
    -moz-osx-font-smoothing:grayscale;
    overflow-x: hidden;
  }

  /* ocean background (public/ocean-bg.jpg) */
  body::before {
    content: '';
    position: fixed;
    inset: 0;
    background-image: url('/ocean-bg.jpg');
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    z-index: -3;
    transform: translateZ(0);
  }

  /* dark overlay to improve contrast */
  body::after {
    content: '';
    position: fixed;
    inset: 0;
    background: linear-gradient(180deg, rgba(0,0,0,0.44), rgba(0,0,0,0.30));
    z-index: -2;
  }

  #root { position: relative; z-index: 1; isolation:isolate; }

  h1,h2,h3,h4,h5,h6 { color: ${({theme}) => theme.colors.text.primary}; line-height: 1.15; }

  a { color: inherit; text-decoration: none; }

  button { font-family: inherit; }

  /* subtle scrollbar */
  ::-webkit-scrollbar { width: 10px; height: 10px; }
  ::-webkit-scrollbar-track { background: rgba(255,255,255,0.02); }
  ::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.08); border-radius: 8px; }
  ::-webkit-scrollbar-thumb:hover { background: rgba(255,255,255,0.12); }

  /* reduce blur on small devices for perf */
  @media (max-width: 600px) {
    body::before { filter: saturate(90%) contrast(90%); }
    * { will-change: auto; }
  }
`;
