import React, { useState } from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

const Nav = styled.nav`
  position: sticky;
  top: 0;
  z-index: 200;
  background: rgba(15, 23, 42, 0.82);
  backdrop-filter: blur(12px) saturate(140%);
  border-bottom: 1px solid rgba(148, 163, 184, 0.25);
  box-shadow: 0 10px 30px rgba(15,23,42,0.9);
`;

const Inner = styled.div`
  max-width: 1280px;
  margin: 0 auto;
  padding: 0.8rem 1.2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Brand = styled.div`
  font-weight: 800;
  font-size: 1.1rem;
  color: ${({ theme }) => theme.colors.text.primary};
`;

const Toggle = styled.button`
  background: none;
  border: none;
  font-size: 1.8rem;
  color: ${({ theme }) => theme.colors.text.primary};
  display: none;
  cursor: pointer;

  @media (max-width: 768px) {
    display: block;
  }
`;

const NavList = styled.div`
  display: flex;
  gap: 1.2rem;
  align-items: center;

  @media (max-width: 768px) {
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background: rgba(15, 23, 42, 0.96);
    flex-direction: column;
    padding: 1.2rem;
    border-bottom: 1px solid rgba(148, 163, 184, 0.25);
    box-shadow: 0 20px 40px rgba(15,23,42,0.95);
    display: ${props => (props.open ? 'flex' : 'none')};
  }
`;

/* ✅ NO HOVER COLOR CHANGE */
const NavItem = styled(NavLink)`
  font-weight: 600;
  font-size: 0.95rem;
  color: ${({ theme }) => theme.colors.text.primary};
  text-decoration: none;
  position: relative;
  padding-bottom: 4px;

  /* ✅ Active underline only */
  &.active::after {
    content: "";
    position: absolute;
    left: 0;
    bottom: -6px;
    width: 100%;
    height: 3px;
    border-radius: 999px;
    background: linear-gradient(90deg, #22d3ee, #0ea5e9);
  }

  /* ✅ NO hover styles */
  &:hover {
    color: ${({ theme }) => theme.colors.text.primary};
  }
`;

export default function Navbar() {
  const [open, setOpen] = useState(false);

  return (
    <Nav>
      <Inner>
        <Brand>Smart Water Monitor</Brand>

        <Toggle onClick={() => setOpen(!open)}>☰</Toggle>

        <NavList open={open}>
          <NavItem to="/" onClick={() => setOpen(false)}>
            Dashboard
          </NavItem>

          <NavItem to="/history" onClick={() => setOpen(false)}>
            History
          </NavItem>

          {/* ✅ NEW BOD PAGE LINK */}
          <NavItem to="/bod" onClick={() => setOpen(false)}>
            BOD
          </NavItem>

          <NavItem to="/about" onClick={() => setOpen(false)}>
            About
          </NavItem>
        </NavList>
      </Inner>
    </Nav>
  );
}
