// client/src/pages/History.js
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ref, onValue, off, remove } from 'firebase/database';
import { database } from '../firebase';
import { NotificationManager } from '../components/Notification';

const HistoryContainer = styled.div`
  padding: 1rem;
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Card = styled.div`
  background: #ffffff;
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: ${props => props.theme.spacing.xl};
  margin-bottom: ${props => props.theme.spacing.xl};
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(0, 0, 0, 0.1);
  width: 100%;
`;

const PageHeader = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  gap: 1rem;
  margin-bottom: 1.25rem;
  width: 100%;
`;

const Title = styled.h2`
  margin: 0;
  color: #1e293b;
  font-size: 2rem;
  font-weight: 600;
`;

const Legend = styled.div`
  display:flex;
  gap: 12px;
  align-items:center;
  flex-wrap:wrap;
`;

const LegendItem = styled.div`
  display:flex;
  align-items:center;
  gap: 8px;
  font-size: 0.9rem;
  color: #475569;
`;

const Pill = styled.span`
  width: 14px;
  height: 14px;
  border-radius: 999px;
  display:inline-block;
  background: ${props => props.color || '#cbd5e1'};
  border: 1px solid rgba(0,0,0,0.06);
`;

const FilterGroup = styled.div`
  display:flex;
  gap: 12px;
  align-items:center;
`;

const Select = styled.select`
  padding: 0.6rem 0.9rem;
  border-radius: 8px;
  border: 1px solid #e5e7eb;
  background: #fff;
  font-size: 0.95rem;
  color: #1e293b;
`;

const ReadingList = styled.div`
  display: grid;
  gap: 1rem;
  width: 100%;
  justify-items: center;
`;

const ReadingCard = styled.div`
  background: #ffffff;
  border-radius: 12px;
  padding: 1.25rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  border: 1px solid #e5e7eb;
  transition: all 0.2s ease;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 12px;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 12px rgba(0, 0, 0, 0.08);
  }
`;

const ReadingHeader = styled.div`
  display:flex;
  flex-direction:column;
  gap: 0.5rem;

  .source {
    font-weight: 700;
    font-size: 1.05rem;
    color: #0f172a;
  }

  .timestamp {
    color: #64748b;
    font-size: 0.85rem;
  }
`;

const ReadingRight = styled.div`
  display:flex;
  flex-direction:column;
  gap: 8px;
  align-items:flex-end;
  justify-content:center;
`;

const Badge = styled.div`
  padding: 8px 12px;
  border-radius: 999px;
  color: #fff;
  font-weight: 700;
  font-size: 0.95rem;
  background: ${props => props.bg || '#6b7280'};
  border: 1px solid rgba(0,0,0,0.04);
`;

const ReadingData = styled.div`
  display:grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 12px;
  margin-top: 0.75rem;
`;

const DataItem = styled.div`
  background: #f8fafc;
  border-radius: 8px;
  padding: 0.75rem;
  border: 1px solid #e5e7eb;
  text-align:center;
`;

const Label = styled.div`
  font-size: 0.75rem;
  color: #64748b;
  margin-bottom: 6px;
  text-transform: uppercase;
`;

const Value = styled.div`
  font-weight: 700;
  color: #0f172a;
`;

/* Delete button */
const DeleteButton = styled.button`
  background: rgba(239, 68, 68, 0.9);
  color: white;
  border: none;
  padding: 10px 14px;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
`;

const DeleteAllButton = styled(DeleteButton)`
  padding: 10px 16px;
  background: #ef4444;
`;

const ModalOverlay = styled.div`
  position: fixed; inset: 0;
  display:flex; align-items:center; justify-content:center;
  background: rgba(2,6,23,0.5); z-index: 1200;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 12px;
  padding: 20px;
  width: min(420px, 92%);
  box-shadow: 0 12px 30px rgba(2,6,23,0.15);
`;

/* Color mapping for pollution types */
const pollutionColors = {
  "Good Water": "#10b981",            // green
  "Sewage": "#ef4444",               // red
  "Agricultural Runoff": "#f59e0b",  // amber/orange
  "Chemical Intrusion": "#8b5cf6",   // purple
  "Thermal Pollution": "#fb923c",    // orange
  "Unknown": "#6b7280"
};

function getBadgeColor(label) {
  if (!label) return pollutionColors["Unknown"];
  return pollutionColors[label] || pollutionColors["Unknown"];
}

function formatNumber(val, digits = 2) {
  if (val === undefined || val === null || Number.isNaN(Number(val))) return "-";
  return Number(val).toFixed(digits);
}

function History() {
  const [readings, setReadings] = useState([]);
  const [sources, setSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState('all');
  const [loading, setLoading] = useState(true);
  const [deletingId, setDeletingId] = useState(null);
  const [showDeleteAllModal, setShowDeleteAllModal] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const addNotification = (message, type = 'success') => {
    setNotifications(prev => [...prev, { message, type }]);
  };

  const removeNotification = (index) => {
    setNotifications(prev => prev.filter((_, i) => i !== index));
  };

  useEffect(() => {
    const readingsRef = ref(database, 'readings');

    const handleData = (snapshot) => {
      try {
        const data = snapshot.val();
        if (data) {
          const readingsArray = Object.entries(data).map(([id, reading]) => ({
            id,
            ...reading
          })).reverse(); // most recent first

          setReadings(readingsArray);

          const uniqueSources = [...new Set(readingsArray.map(r => r.source).filter(Boolean))];
          setSources(uniqueSources);
        } else {
          setReadings([]);
          setSources([]);
        }
      } catch (err) {
        console.error('Error processing data:', err);
      } finally {
        setLoading(false);
      }
    };

    const handleError = (err) => {
      console.error('Firebase listener error:', err);
      setLoading(false);
    };

    onValue(readingsRef, handleData, handleError);

    return () => {
      off(readingsRef);
    };
  }, []);

  const filteredReadings = selectedSource === 'all'
    ? readings
    : readings.filter(r => r.source === selectedSource);

  const handleDelete = async (id) => {
    try {
      setDeletingId(id);
      const readingRef = ref(database, `readings/${id}`);
      await remove(readingRef);
      addNotification('Reading deleted successfully', 'success');
    } catch (err) {
      console.error('Delete error', err);
      addNotification('Failed to delete reading', 'error');
    } finally {
      setDeletingId(null);
    }
  };

  const handleDeleteAll = async () => {
    try {
      setLoading(true);
      const readingsRef = ref(database, 'readings');
      await remove(readingsRef);
      addNotification('All readings deleted', 'success');
      setShowDeleteAllModal(false);
    } catch (err) {
      console.error('Delete all error', err);
      addNotification('Failed to delete all readings', 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <HistoryContainer>
      <Card>
        <PageHeader>
          <div>
            <Title>Historical Readings</Title>
            <Legend style={{ marginTop: 8 }}>
              <LegendItem><Pill color={pollutionColors["Good Water"]} /> Good Water</LegendItem>
              <LegendItem><Pill color={pollutionColors["Sewage"]} /> Sewage</LegendItem>
              <LegendItem><Pill color={pollutionColors["Agricultural Runoff"]} /> Agricultural Runoff</LegendItem>
              <LegendItem><Pill color={pollutionColors["Chemical Intrusion"]} /> Chemical Intrusion</LegendItem>
              <LegendItem><Pill color={pollutionColors["Thermal Pollution"]} /> Thermal Pollution</LegendItem>
            </Legend>
          </div>

          <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
            <FilterGroup>
              <Select value={selectedSource} onChange={e => setSelectedSource(e.target.value)}>
                <option value="all">All Sources</option>
                {sources.map(s => <option key={s} value={s}>{s}</option>)}
              </Select>
            </FilterGroup>

            <DeleteAllButton onClick={() => setShowDeleteAllModal(true)}>Delete Whole History</DeleteAllButton>
          </div>
        </PageHeader>

        {loading ? (
          <div>Loading data...</div>
        ) : filteredReadings.length === 0 ? (
          <div>No readings found</div>
        ) : (
          <ReadingList>
            {filteredReadings.map(reading => {
              // Backwards-compatible field access:
              const temperature = reading.temperature ?? reading.temp ?? reading.temperature_c ?? null;
              const tds = reading.tds ?? reading.tds_ppm ?? null;
              const turbidity = reading.turbidity ?? reading.ntu ?? reading.ntu_lin ?? null;
              const dissolvedOxygen = reading.dissolvedOxygen ?? reading.do ?? null;
              const pH = reading.pH ?? reading.ph ?? null;
              const source = reading.source ?? 'Unknown';
              const label = reading.pollutionLabel ?? reading.classification ?? reading.label ?? 'Unknown';
              const timestamp = reading.timestamp ?? reading.time ?? null;

              return (
                <ReadingCard key={reading.id}>
                  <div>
                    <ReadingHeader>
                      <div className="source">{source}</div>
                      <div className="timestamp">{ timestamp ? new Date(timestamp).toLocaleString() : '—' }</div>
                    </ReadingHeader>

                    <ReadingData>
                      <DataItem>
                        <Label>Temperature</Label>
                        <Value>{formatNumber(temperature, 2)} °C</Value>
                      </DataItem>

                      <DataItem>
                        <Label>TDS</Label>
                        <Value>{formatNumber(tds, 2)} ppm</Value>
                      </DataItem>

                      <DataItem>
                        <Label>Turbidity</Label>
                        <Value>{formatNumber(turbidity, 2)} NTU</Value>
                      </DataItem>

                      <DataItem>
                        <Label>Dissolved Oxygen</Label>
                        <Value>{formatNumber(dissolvedOxygen, 2)} mg/L</Value>
                      </DataItem>

                      <DataItem>
                        <Label>pH</Label>
                        <Value>{pH !== null ? formatNumber(pH, 2) : '-'}</Value>
                      </DataItem>
                    </ReadingData>
                  </div>

                  <ReadingRight>
                    <Badge bg={getBadgeColor(label)}>{label}</Badge>
                    <DeleteButton onClick={() => handleDelete(reading.id)} disabled={deletingId === reading.id}>
                      {deletingId === reading.id ? 'Deleting...' : 'Delete'}
                    </DeleteButton>
                  </ReadingRight>
                </ReadingCard>
              );
            })}
          </ReadingList>
        )}

      </Card>

      {showDeleteAllModal && (
        <ModalOverlay>
          <ModalContent>
            <h3 style={{ marginTop: 0 }}>Delete All History</h3>
            <p>Are you sure you want to delete all historical readings? This action cannot be undone.</p>
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 12, marginTop: 16 }}>
              <button onClick={() => setShowDeleteAllModal(false)} style={{ padding: '8px 12px', borderRadius: 8, background: '#f1f5f9', border: '1px solid #e2e8f0' }}>Cancel</button>
              <button onClick={handleDeleteAll} style={{ padding: '8px 12px', borderRadius: 8, background: '#ef4444', color: 'white', border: 'none' }}>Delete All</button>
            </div>
          </ModalContent>
        </ModalOverlay>
      )}

      <NotificationManager notifications={notifications} onRemove={removeNotification} />
    </HistoryContainer>
  );
}

export default History;
