// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { ref, push, set } from 'firebase/database';
import { database } from '../firebase';
import { NotificationManager } from '../components/Notification';
import { CardTitle, ValueDisplay } from '../components/Card';
import Gauge from '../components/Gauge';
import { useNavigate } from 'react-router-dom';

/* ================= LAYOUT ================= */

const Container = styled.div`
  padding: 36px 28px;
  max-width: 1280px;
  margin: 0 auto;
`;

const Header = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom: 26px;
`;

const PageTitle = styled.h2`
  margin: 0;
  color: ${({theme}) => theme.colors.text.primary};
  font-size: 2rem;
`;

/* ================= STATUS BADGE ================= */

const StatusBadge = styled.div`
  padding: 10px 16px;
  border-radius: 999px;
  font-weight: 800;
  display:inline-flex;
  align-items:center;
  gap: 8px;
  background: ${({connected}) =>
    connected
      ? 'linear-gradient(90deg, rgba(16,185,129,0.16), rgba(16,185,129,0.08))'
      : 'linear-gradient(90deg, rgba(239,68,68,0.16), rgba(239,68,68,0.08))'};
  color: ${({connected}) => connected ? '#bbf7d0' : '#fecaca'};
  border: 1px solid ${({connected}) =>
    connected ? 'rgba(34,197,94,0.45)' : 'rgba(248,113,113,0.45)'};
  box-shadow: 0 14px 35px rgba(15,23,42,0.7);
  backdrop-filter: blur(8px);
`;

/* ================= GRID ================= */

const Grid = styled.div`
  display:grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 22px;
  margin-bottom: 22px;

  @media (max-width: 1100px) { grid-template-columns: repeat(2, 1fr); }
  @media (max-width: 680px) { grid-template-columns: 1fr; }
`;

/* ================= SLAB TILE ================= */

const Tile = styled.div`
  background: rgba(15,23,42,0.78);
  border-radius: 22px;
  padding: 26px;
  box-shadow:
    0 22px 50px rgba(15,23,42,0.85),
    inset 0 1px 0 rgba(255,255,255,0.03);
  border: 1px solid rgba(148,163,184,0.28);
  backdrop-filter: blur(10px) saturate(120%);
  transition: transform 160ms ease, box-shadow 180ms ease;
  min-height: 210px;
  display:flex;
  flex-direction:column;
  justify-content:center;
  color: ${({theme}) => theme.colors.text.primary};

  &:hover {
    transform: translateY(-3px);
    box-shadow:
      0 28px 70px rgba(15,23,42,0.95),
      inset 0 1px 0 rgba(255,255,255,0.04);
  }
`;

const Accent = styled.div`
  position:absolute;
  top:16px;
  left:22px;
  width:70px;
  height:8px;
  border-radius:999px;
  background: linear-gradient(90deg, ${p => p.colorStart}, ${p => p.colorEnd});
  box-shadow:0 10px 20px rgba(15,23,42,0.85);
`;

const TileWrapper = styled.div`
  position: relative;
`;

const BigTitle = styled(CardTitle)`
  font-size: 1.05rem;
  margin-bottom: 10px;
`;

const BigValue = styled(ValueDisplay)`
  font-size: 2rem;
`;

/* ================= INPUT + BUTTONS ================= */

const ActionsRow = styled.div`
  display:flex;
  gap: 14px;
  align-items:center;
  margin-top: 18px;
  width: 100%;

  @media (max-width:680px) {
    flex-direction: column;
    align-items: stretch;
  }
`;

const SourceInput = styled.input`
  flex: 1;
  padding: 16px 18px;
  border-radius: 18px;
  border: 1px solid rgba(15,23,42,0.92);
  background: rgba(255,255,255,0.96);
  font-size: 1rem;
  color: #020617;

  &::placeholder { color: #64748b; }
`;

const ButtonsGroup = styled.div`
  display:flex;
  gap:12px;
`;

const PrimaryButton = styled.button`
  background: linear-gradient(180deg,#3b82f6,#2563eb);
  color:white;
  padding: 12px 20px;
  border-radius: 16px;
  font-weight:800;
  border:none;
  cursor:pointer;
`;

const SaveButton = styled(PrimaryButton)`
  background: linear-gradient(180deg,#22c55e,#16a34a);
`;

/* ================= DASHBOARD ================= */

function Dashboard() {
  const navigate = useNavigate();

  const [sensorData, setSensorData] = useState({
    temp: -127,
    tds: 0,
    ntu: 0,
    do: 0,
    pH: 7
  });

  const [connected, setConnected] = useState(false);
  const [source, setSource] = useState('');
  const [pollutionLabel, setPollutionLabel] = useState('');
  const [notifications, setNotifications] = useState([]);

  const addNotification = (m, t='success') =>
    setNotifications(prev => [...prev, { message: m, type: t }]);

  const removeNotification = (i) =>
    setNotifications(prev => prev.filter((_, idx) => idx !== i));

  /* ðŸ” Fetch ESP32 Data */
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetch(`http://localhost:80/data`);
        const d = await res.json();
        setSensorData(d);
        setConnected(true);
      } catch {
        setConnected(false);
      }
    };

    fetchData();
    const id = setInterval(fetchData, 2000);
    return () => clearInterval(id);
  }, []);

  const handleClassify = () => {
    setPollutionLabel("Good Water");
    addNotification("Classified: Good Water");
  };

  const handleSave = async () => {
    if (!source) return addNotification("Enter water source", "error");

    const newRef = push(ref(database, "readings"));
    await set(newRef, {
      ...sensorData,
      pollutionLabel,
      source,
      timestamp: new Date().toISOString()
    });

    addNotification("Saved to Firebase");
  };

  return (
    <Container>
      <Header>
        <PageTitle>Current Sensor Readings</PageTitle>
        <StatusBadge connected={connected}>
          {connected ? "Connected to ESP32 / RTDB" : "âš  Disconnected"}
        </StatusBadge>
      </Header>

      <Grid>
        <TileWrapper>
          <Accent colorStart="#06b6d4" colorEnd="#22c1d1" />
          <Tile>
            <BigTitle>Temperature</BigTitle>
            <Gauge value={sensorData.temp} min={-10} max={50} unit="Â°C" />
            <BigValue>{sensorData.temp} Â°C</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#60a5fa" colorEnd="#3b82f6" />
          <Tile>
            <BigTitle>TDS</BigTitle>
            <BigValue>{sensorData.tds} ppm</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#7c3aed" colorEnd="#9f7aea" />
          <Tile>
            <BigTitle>Turbidity</BigTitle>
            <BigValue>{sensorData.ntu} NTU</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#10b981" colorEnd="#34d399" />
          <Tile>
            <BigTitle>Dissolved Oâ‚‚</BigTitle>
            <BigValue>{sensorData.do} mg/L</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#60a5fa" colorEnd="#38bdf8" />
          <Tile>
            <BigTitle>pH</BigTitle>
            <BigValue>{sensorData.pH}</BigValue>
          </Tile>
        </TileWrapper>

        <TileWrapper>
          <Accent colorStart="#f472b6" colorEnd="#fb7185" />
          <Tile>
            <BigTitle>Manual Test</BigTitle>
            <PrimaryButton onClick={() => navigate('/test-input')}>
              Open Test Input
            </PrimaryButton>
          </Tile>
        </TileWrapper>
      </Grid>

      <ActionsRow>
        <SourceInput
          placeholder="Enter water source"
          value={source}
          onChange={e => setSource(e.target.value)}
        />

        <ButtonsGroup>
          <PrimaryButton onClick={handleClassify}>
            Classify Pollution
          </PrimaryButton>

          <SaveButton onClick={handleSave}>
            Save to Firebase
          </SaveButton>
        </ButtonsGroup>
      </ActionsRow>

      <NotificationManager
        notifications={notifications}
        onRemove={removeNotification}
      />
    </Container>
  );
}

export default Dashboard;
