// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ref, push, set } from 'firebase/database';
import { database } from '../firebase';
import { NotificationManager } from '../components/Notification';
import { Card, CardTitle, ValueDisplay } from '../components/Card';
import Gauge from '../components/Gauge';

const DashboardContainer = styled.div`
  padding: 28px 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const HeaderRow = styled.div`
  display:flex;
  justify-content:space-between;
  align-items:center;
  margin-bottom: 18px;
`;

const Title = styled.h2`
  color: ${({theme}) => theme.colors.text.primary};
  font-size: ${({theme}) => theme.typography.h2.fontSize};
  margin: 0;
`;

const StatusBadge = styled.div`
  padding: 8px 12px;
  border-radius: 999px;
  background: ${({connected}) => connected ? 'rgba(16,185,129,0.14)' : 'rgba(239,68,68,0.12)'};
  color: ${({connected}) => connected ? '#064e3b' : '#58151a'};
  font-weight: 700;
  border: 1px solid rgba(255,255,255,0.04);
`;

const Grid = styled.div`
  display:grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 18px;
  margin-bottom: 18px;

  @media (max-width: 920px) {
    grid-template-columns: repeat(2, 1fr);
  }
  @media (max-width: 600px) {
    grid-template-columns: 1fr;
  }
`;

const Tile = styled(Card)`
  display:flex;
  flex-direction:column;
  align-items:center;
  justify-content:center;
  min-height: 160px;
  position: relative;
`;

const AccentStrip = styled.div`
  position: absolute;
  top: 12px;
  left: 12px;
  width: 44px;
  height: 6px;
  border-radius: 6px;
  background: ${props => props.color || props.theme.ui.accent};
`;

const ActionsRow = styled.div`
  display:flex;
  gap: 12px;
  align-items:center;
  margin-top: 12px;
  @media (max-width: 600px) { flex-direction: column; align-items: stretch; }
`;

const WideInput = styled.input`
  flex: 1;
  padding: 14px 16px;
  border-radius: 12px;
  border: 1px solid rgba(255,255,255,0.06);
  background: rgba(255,255,255,0.92);
  font-size: 1rem;
  box-shadow: inset 0 2px 6px rgba(0,0,0,0.04);
`;

const PrimaryButton = styled.button`
  background: linear-gradient(180deg, #2d6df6, #2563eb);
  color: white;
  font-weight: 700;
  padding: 12px 18px;
  border-radius: 12px;
  border: none;
  box-shadow: 0 12px 30px rgba(37,99,235,0.12);
  cursor: pointer;
  min-width: 160px;
  &:disabled { opacity: 0.6; cursor: not-allowed; box-shadow: none; }
`;

const SecondaryButton = styled(PrimaryButton)`
  background: linear-gradient(180deg, #14b8a6, #10b981);
  box-shadow: 0 12px 30px rgba(16,185,129,0.10);
`;

const ResultCard = styled(Card)`
  margin-top: 14px;
  display:flex;
  justify-content:space-between;
  align-items:center;
  padding: 14px 18px;
`;

function Dashboard() {
  const [sensorData, setSensorData] = useState({ temp: 0, tds: 0, ntu: 0, do: 0, pH: 7.0 });
  const [source, setSource] = useState('');
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const [pollutionLabel, setPollutionLabel] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const [isClassifying, setIsClassifying] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const addNotification = (message, type='success') =>
    setNotifications(prev => [...prev, { message, type }]);
  const removeNotification = (i) =>
    setNotifications(prev => prev.filter((_, idx) => idx !== i));

  useEffect(() => {
    let mounted = true;
    const fetchData = async () => {
      try {
        const hostname = window.location.hostname;
        const res = await fetch(`http://${hostname}:5000/api/data`);
        if (!res.ok) throw new Error('Fetch error');
        const d = await res.json();
        if (!mounted) return;
        setSensorData(prev => ({
          temp: Number(d.temp ?? prev.temp),
          tds: Number(d.tds ?? prev.tds),
          ntu: Number(d.ntu ?? prev.ntu),
          do: Number(d.do ?? prev.do),
          pH: Number(d.pH ?? prev.pH)
        }));
        setConnectionStatus('Connected to ESP32');
      } catch (err) {
        console.warn(err);
        setConnectionStatus('Connection Error');
      }
    };
    fetchData();
    const id = setInterval(fetchData, 2000);
    return () => { mounted = false; clearInterval(id); };
  }, []);

  const classifyLocal = ({ tds, pH, ntu, do: DO, temp }) => {
    if (DO > 7 && tds < 300 && ntu < 5 && pH >= 6.5 && pH <= 8.5) return "Good Water";
    if (DO < 3 && ntu > 10) return "Sewage";
    if (tds > 500 && ntu > 10 && pH > 7.5) return "Agricultural Runoff";
    if (tds > 1000 || pH < 6 || pH > 9) return "Chemical Intrusion";
    if (temp > 30 && DO < 6) return "Thermal Pollution";
    return "Good Water";
  };

  const handleClassify = async () => {
    setIsClassifying(true);
    try {
      const label = classifyLocal(sensorData);
      setPollutionLabel(label);
      addNotification('Classification done', 'success');
    } catch (err) {
      addNotification('Classification failed', 'error');
    } finally {
      setIsClassifying(false);
    }
  };

  const handleSave = async () => {
    if (!pollutionLabel) {
      addNotification('Classify before saving', 'error');
      return;
    }
    if (!source.trim()) {
      addNotification('Enter water source', 'error');
      return;
    }
    setIsSaving(true);
    try {
      const readingsRef = ref(database, 'readings');
      const newRef = push(readingsRef);
      await set(newRef, {
        temperature: Number(sensorData.temp),
        tds: Number(sensorData.tds),
        turbidity: Number(sensorData.ntu),
        dissolvedOxygen: Number(sensorData.do),
        pH: Number(sensorData.pH),
        pollutionLabel,
        source: source.trim(),
        timestamp: new Date().toISOString()
      });
      addNotification('Saved to Firebase', 'success');
      setSource('');
    } catch (err) {
      console.error(err);
      addNotification('Save failed', 'error');
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <DashboardContainer>
      <HeaderRow>
        <Title>Current Sensor Readings</Title>
        <StatusBadge connected={connectionStatus === 'Connected to ESP32'}>{connectionStatus}</StatusBadge>
      </HeaderRow>

      <Grid>
        <Tile>
          <AccentStrip color="#4F46E5" />
          <CardTitle>Temperature</CardTitle>
          <ValueDisplay>{Number(sensorData.temp).toFixed(2)} °C</ValueDisplay>
        </Tile>

        <Tile>
          <AccentStrip color="#2563eb" />
          <CardTitle>TDS</CardTitle>
          <ValueDisplay>{Number(sensorData.tds).toFixed(2)} ppm</ValueDisplay>
        </Tile>

        <Tile>
          <AccentStrip color="#10b981" />
          <CardTitle>Dissolved Oxygen</CardTitle>
          <ValueDisplay>{Number(sensorData.do).toFixed(2)} mg/L</ValueDisplay>
        </Tile>

        <Tile>
          <AccentStrip color="#7c3aed" />
          <CardTitle>Turbidity (Quadratic)</CardTitle>
          <ValueDisplay>{Number(sensorData.ntu).toFixed(2)} NTU</ValueDisplay>
        </Tile>

        <Tile>
          <AccentStrip color="#60a5fa" />
          <CardTitle>Turbidity (Linear)</CardTitle>
          <ValueDisplay>{Number(sensorData.ntu).toFixed(2)} NTU</ValueDisplay>
        </Tile>

        <Tile>
          <AccentStrip color="#06b6d4" />
          <CardTitle>Temperature Gauge</CardTitle>
          <div style={{ width: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <div style={{ width: 160 }}>
              <Gauge value={Number(sensorData.temp)} min={-10} max={50} unit="°C" />
            </div>
          </div>
        </Tile>
      </Grid>

      <ActionsRow>
        <WideInput
          placeholder="Enter water source (e.g., tap, lake, well)"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
        <PrimaryButton onClick={handleClassify} disabled={isClassifying}>
          {isClassifying ? 'Classifying...' : 'Classify Pollution'}
        </PrimaryButton>
        <SecondaryButton onClick={handleSave} disabled={isSaving || !pollutionLabel}>
          {isSaving ? 'Saving...' : 'Save to Firebase'}
        </SecondaryButton>
      </ActionsRow>

      {pollutionLabel && (
        <ResultCard>
          <div>
            <strong>Predicted:</strong> &nbsp; {pollutionLabel}
          </div>
          <div style={{ color: '#64748b' }}>{new Date().toLocaleString()}</div>
        </ResultCard>
      )}

      <NotificationManager notifications={notifications} onRemove={removeNotification} />
    </DashboardContainer>
  );
}

export default Dashboard;
