// src/pages/Dashboard.js
// Safe, diagnostic dashboard that normalizes common import/export mistakes
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { ref, push, set } from "firebase/database";
import { database } from "../firebase";
import { useNavigate } from "react-router-dom";

/*
  WARNING HANDLER:
  When you import a component file that exports a default but bundler wraps it
  in an object, React will throw "Element type is invalid". To avoid that crash
  while we debug, this file normalizes the imports and falls back to simple
  placeholders if something is still invalid.
*/

// Attempt imports (these may be objects if default export mixed up)
import * as MetricCard_ from "../components/MetricCard";
import * as Alerts_ from "../components/Alerts";
import * as Gauge_ from "../components/Gauge";

// helper to normalize an import which might be { default: Component } or the component itself
function normalizeImport(mod, name) {
  if (!mod) {
    console.warn(`[normalizeImport] ${name} is undefined`);
    return null;
  }
  // if the import is a React component function/class directly, return it
  if (typeof mod === "function" || typeof mod === "object" && mod.$$typeof) {
    return mod;
  }
  // If module is an object with a default property, return that
  if (mod && mod.default) {
    // sometimes default can be an object wrapper; check again
    if (typeof mod.default === "function" || typeof mod.default === "object" && mod.default.$$typeof) {
      return mod.default;
    }
    return mod.default;
  }
  // if module has named export with same name, try that
  if (mod && mod[name]) return mod[name];
  // unknown shape — return mod as-is (likely not a component)
  return mod;
}

const MetricCard = normalizeImport(MetricCard_, "MetricCard");
const Alerts = normalizeImport(Alerts_, "Alerts");
const Gauge = normalizeImport(Gauge_, "Gauge");

// Fallback components (render simple content so the page doesn't crash)
const FallbackMetric = ({ title = "Metric", value = 0, unit = "" }) => (
  <div style={{
    background: "rgba(255,255,255,0.03)",
    borderRadius: 10,
    padding: 12,
    minHeight: 88,
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
  }}>
    <div style={{ color: "rgba(255,255,255,0.7)", fontWeight: 700 }}>{title}</div>
    <div style={{ color: "white", fontWeight: 800, fontSize: 20 }}>{value} {unit}</div>
    <div style={{ color: "rgba(255,255,255,0.5)", fontSize: 12 }}>⚠ fallback UI</div>
  </div>
);

const FallbackAlerts = () => (
  <div style={{
    background: "rgba(255,255,255,0.03)",
    borderRadius: 12,
    padding: 12,
    minHeight: 140
  }}>
    <div style={{ fontWeight: 900, marginBottom: 8 }}>Recent Alerts</div>
    <div style={{ color: "rgba(255,255,255,0.65)" }}>No alerts (fallback)</div>
  </div>
);

const FallbackGauge = ({ value = 0, unit = "" }) => (
  <div style={{ width: 180, height: 80, display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
    <div style={{ width: 140, height: 70, borderRadius: 8, background: "rgba(255,255,255,0.02)", display: "flex", alignItems: "center", justifyContent: "center" }}>
      <div style={{ fontWeight: 800 }}>{value}{unit}</div>
    </div>
    <div style={{ fontSize: 12, color: "rgba(255,255,255,0.6)" }}>gauge (fallback)</div>
  </div>
);

// If any import is invalid, show console diagnostics (very explicit)
function logIfInvalid(name, comp) {
  if (!comp) {
    console.error(`[Dashboard] ${name} import is invalid (null/undefined). You likely forgot to export default from the file or path is wrong.`);
    return;
  }
  // React component check — function/class or object with $$typeof (React element)
  if (typeof comp !== "function" && !(typeof comp === "object" && comp.$$typeof)) {
    console.warn(`[Dashboard] ${name} import exists but is not a typical React component. typeof=${typeof comp}`, comp);
  } else {
    console.info(`[Dashboard] ${name} import seems OK. typeof=${typeof comp}`);
  }
}

logIfInvalid("MetricCard", MetricCard);
logIfInvalid("Alerts", Alerts);
logIfInvalid("Gauge", Gauge);

// Use fallback when needed
const MetricToUse = (MetricCard && (typeof MetricCard === "function" || typeof MetricCard === "object")) ? MetricCard : FallbackMetric;
const AlertsToUse = (Alerts && (typeof Alerts === "function" || typeof Alerts === "object")) ? Alerts : FallbackAlerts;
const GaugeToUse = (Gauge && (typeof Gauge === "function" || typeof Gauge === "object")) ? Gauge : FallbackGauge;

/* ---------- Styled layout (keeps previous visual) ---------- */
const GridTop = styled.div`
  display:grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 14px;
  margin-bottom: 18px;
  @media (max-width: 900px) { grid-template-columns: 1fr; }
`;

const GridMain = styled.div`
  display:grid;
  grid-template-columns: 2fr 1fr;
  gap: 18px;
  margin-bottom: 18px;
  @media (max-width: 1100px) { grid-template-columns: 1fr; }
`;

const FogTile = styled.div`
  background: rgba(0,0,0,0.18);
  border-radius: 12px;
  padding: 18px;
  box-shadow: 0 16px 44px rgba(2,6,23,0.28);
  border: 1px solid rgba(255,255,255,0.04);
`;

const ActionsRow = styled.div`
  display:flex;
  gap: 12px;
  align-items:center;
  margin-top: 10px;
  justify-content:flex-end;
`;

const Inp = styled.input`
  flex: 1;
  padding: 12px;
  border-radius: 12px;
  border: 1px solid rgba(255,255,255,0.06);
  background: rgba(255,255,255,0.98);
`;

const Btn = styled.button`
  padding: 10px 14px;
  border-radius: 12px;
  border:none;
  font-weight:800;
  cursor:pointer;
  color:white;
`;

const ClassBtn = styled(Btn)` background: linear-gradient(180deg,#3b82f6,#2563eb); `;
const SaveBtn = styled(Btn)` background: linear-gradient(180deg,#10b981,#059669); `;

/* ---------- Component ---------- */
export default function Dashboard() {
  const navigate = useNavigate();
  const [sensorData, setSensorData] = useState({ temp: 0, tds: 0, ntu: 0, do: 0, pH: 7 });
  const [source, setSource] = useState("");
  const [label, setLabel] = useState("");
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    let mounted = true;
    async function fetcher() {
      try {
        const host = window.location.hostname;
        const r = await fetch(`http://${host}:5000/api/data`);
        if (!r.ok) throw new Error("no");
        const j = await r.json();
        if (!mounted) return;
        setSensorData({
          temp: Number(j.temp ?? sensorData.temp),
          tds: Number(j.tds ?? sensorData.tds),
          ntu: Number(j.ntu ?? sensorData.ntu),
          do: Number(j.do ?? sensorData.do),
          pH: Number(j.pH ?? sensorData.pH),
        });
        setConnected(true);
      } catch (e) {
        setConnected(false);
      }
    }
    fetcher();
    const id = setInterval(fetcher, 2500);
    return () => {
      mounted = false;
      clearInterval(id);
    };
  }, []);

  const classifyLocal = ({ tds, pH, ntu, do: DO, temp }) => {
    if (DO > 7 && tds < 300 && ntu < 5 && pH >= 6.5 && pH <= 8.5) return "Good Water";
    if (DO < 3 && ntu > 10) return "Sewage";
    if (tds > 500 && ntu > 10 && pH > 7.5) return "Agricultural Runoff";
    if (tds > 1000 || pH < 6 || pH > 9) return "Chemical Intrusion";
    if (temp > 30 && DO < 6) return "Thermal Pollution";
    return "Good Water";
  };

  const handleClassify = () => {
    const l = classifyLocal(sensorData);
    setLabel(l);
  };

  const handleSave = async () => {
    if (!label) return alert("Classify first");
    if (!source.trim()) return alert("Enter source");
    const r = ref(database, "readings");
    const n = push(r);
    await set(n, {
      temperature: Number(sensorData.temp),
      tds: Number(sensorData.tds),
      turbidity: Number(sensorData.ntu),
      dissolvedOxygen: Number(sensorData.do),
      pH: Number(sensorData.pH),
      pollutionLabel: label,
      source,
      timestamp: new Date().toISOString(),
    });
    setSource("");
    alert("Saved");
  };

  // metrics array for the small cards
  const metrics = [
    {
      title: "pH Level",
      value: sensorData.pH,
      unit: "pH",
      sparkline: [6.8, 7.0, 7.1, 7.2, 7.0, sensorData.pH],
      statusText: "Normal",
      statusColor: "#10b981",
    },
    {
      title: "Turbidity",
      value: sensorData.ntu,
      unit: "NTU",
      sparkline: [0.5, 1.2, 1.1, 1.4, 1.2, sensorData.ntu],
      statusText: "Stable",
      statusColor: "#60a5fa",
    },
    {
      title: "TDS",
      value: sensorData.tds,
      unit: "ppm",
      sparkline: [200, 220, 230, 240, 238, sensorData.tds],
      statusText: "Check Required",
      statusColor: "#f59e0b",
    },
    {
      title: "Temperature",
      value: sensorData.temp,
      unit: "°C",
      sparkline: [23, 24, 24.5, 24.2, 23.8, sensorData.temp],
      statusText: "Normal",
      statusColor: "#7c3aed",
    },
  ];

  // render helpers that use the resolved or fallback component
  const MetricComp = MetricToUse;
  const AlertsComp = AlertsToUse;
  const GaugeComp = GaugeToUse;

  return (
    <div>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
        <h1 style={{ margin: 0 }}>Current Sensor Readings</h1>
        <div style={{ padding: "8px 12px", borderRadius: 999, background: connected ? "rgba(16,185,129,0.12)" : "rgba(239,68,68,0.08)", border: connected ? "1px solid rgba(16,185,129,0.22)" : "1px solid rgba(239,68,68,0.22)" }}>
          {connected ? "Connected" : "Connection Error"}
        </div>
      </div>

      <GridTop>
        {metrics.slice(0, 2).map((m, i) => {
          // If the resolved MetricComp is a React component function/class, render it with props.
          // Otherwise render fallback directly.
          try {
            if (typeof MetricComp === "function" || (typeof MetricComp === "object" && MetricComp.$$typeof)) {
              return <MetricComp key={i} title={m.title} value={m.value} unit={m.unit} sparkline={m.sparkline} statusText={m.statusText} statusColor={m.statusColor} />;
            }
          } catch (err) {
            console.error("Failed to render MetricComp:", err);
          }
          return <FallbackMetric key={i} title={m.title} value={m.value} unit={m.unit} />;
        })}
      </GridTop>

      <GridMain>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 18 }}>
          <FogTile>
            <div style={{ display: "flex", alignItems: "center", gap: 18 }}>
              <div style={{ width: 210 }}>
                { (typeof GaugeComp === "function" || (typeof GaugeComp === "object" && GaugeComp.$$typeof))
                  ? <GaugeComp value={Number(sensorData.temp)} min={-10} max={50} unit="°C" />
                  : <FallbackGauge value={Number(sensorData.temp)} unit="°C" /> }
              </div>

              <div style={{ flex: 1 }}>
                <h3 style={{ marginTop: 0 }}>Temperature</h3>
                <div style={{ fontSize: 30, fontWeight: 900 }}>{Number(sensorData.temp).toFixed(2)} °C</div>
                <div style={{ marginTop: 8, color: "rgba(255,255,255,0.72)" }}>{/* additional text placeholder */}</div>
              </div>
            </div>
          </FogTile>

          <div style={{ display: "flex", flexDirection: "column", gap: 18 }}>
            <FogTile>
              <h3 style={{ marginTop: 0 }}>TDS</h3>
              <div style={{ fontSize: 28, fontWeight: 800 }}>{Number(sensorData.tds).toFixed(2)} ppm</div>
            </FogTile>

            <FogTile>
              <h3 style={{ marginTop: 0 }}>Turbidity (Quadratic)</h3>
              <div style={{ fontSize: 28, fontWeight: 800 }}>{Number(sensorData.ntu).toFixed(2)} NTU</div>
            </FogTile>
          </div>
        </div>

        <div style={{ display: "flex", flexDirection: "column", gap: 14 }}>
          { (typeof AlertsComp === "function" || (typeof AlertsComp === "object" && AlertsComp.$$typeof))
            ? <AlertsComp />
            : <FallbackAlerts /> }

          <div style={{ background: "rgba(255,255,255,0.025)", borderRadius: 12, padding: 12 }}>
            <h4 style={{ marginTop: 0 }}>Actions</h4>
            <div style={{ display: "flex", gap: 12 }}>
              <Inp placeholder="Enter water source (e.g., tap, lake, well)" value={source} onChange={(e) => setSource(e.target.value)} />
              <div style={{ display: "flex", flexDirection: "column", gap: 8, alignItems: "stretch" }}>
                <ClassBtn onClick={handleClassify}>Classify Pollution</ClassBtn>
                <SaveBtn onClick={handleSave} style={{ marginTop: 4 }}>Save to Firebase</SaveBtn>
              </div>
            </div>

            {label && (
              <div style={{ marginTop: 10, padding: 10, borderRadius: 10, background: "rgba(0,0,0,0.12)" }}>
                <strong>Predicted:</strong> {label}
              </div>
            )}
          </div>
        </div>
      </GridMain>

      <div style={{ marginTop: 8 }}>
        <GridTop>
          {metrics.slice(2, 4).map((m, i) => {
            try {
              if (typeof MetricComp === "function" || (typeof MetricComp === "object" && MetricComp.$$typeof)) {
                return <MetricComp key={i + 2} title={m.title} value={m.value} unit={m.unit} sparkline={m.sparkline} statusText={m.statusText} statusColor={m.statusColor} />;
              }
            } catch (err) {
              console.error("Failed to render MetricComp:", err);
            }
            return <FallbackMetric key={i + 2} title={m.title} value={m.value} unit={m.unit} />;
          })}
        </GridTop>
      </div>
    </div>
  );
}
