// src/globalStyles.js
import { createGlobalStyle } from 'styled-components';
import oceanBg from './assets/ocean-bg.jpg';

export const GlobalStyle = createGlobalStyle`
  *, *::before, *::after {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }

  html, body, #root {
    height: 100%;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Open Sans', sans-serif;
    line-height: 1.5;
    min-height: 100vh;
    margin: 0;
    padding: 0;
    color: ${({ theme }) => theme.colors.text.primary};

    /* just a fallback color behind the image layer */
    background: #020617;

    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    overflow-x: hidden;
    position: relative;
  }

  /* OCEAN BACKGROUND IMAGE */
  body::before {
    content: '';
    position: fixed;
    inset: 0;
    background-image: url(${oceanBg});
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    z-index: -3;
    transform: translateZ(0);
  }

  /* SOFT DARK OVERLAY FOR READABILITY (LIGHTER THAN BEFORE) */
  body::after {
    content: '';
    position: fixed;
    inset: 0;
    /* soft gradient, not full black */
    background:
      radial-gradient(circle at top left, rgba(15,23,42,0.45), transparent 55%),
      linear-gradient(180deg, rgba(15,23,42,0.65), rgba(15,23,42,0.35));
    mix-blend-mode: multiply;
    z-index: -2;
  }

  #root {
    position: relative;
    z-index: 1;
    isolation: isolate;
  }

  h1, h2, h3, h4, h5, h6 {
    color: ${({ theme }) => theme.colors.text.primary};
    line-height: 1.15;
  }

  a {
    color: inherit;
    text-decoration: none;
  }

  button {
    font-family: inherit;
  }

  /* subtle scrollbar */
  ::-webkit-scrollbar {
    width: 10px;
    height: 10px;
  }
  ::-webkit-scrollbar-track {
    background: rgba(255,255,255,0.02);
  }
  ::-webkit-scrollbar-thumb {
    background: rgba(255,255,255,0.14);
    border-radius: 8px;
  }
  ::-webkit-scrollbar-thumb:hover {
    background: rgba(255,255,255,0.20);
  }

  /* mobile: slightly lower contrast but keep image visible */
  @media (max-width: 600px) {
    body::before {
      filter: saturate(95%) contrast(95%);
    }
    * {
      will-change: auto;
    }
  }
`;
