// src/pages/History.js
import React, { useEffect, useState } from 'react';
import { ref, onValue, remove } from 'firebase/database';
import { database } from '../firebase';
import styled from 'styled-components';

const Page = styled.div` padding:28px; color:#222; `;
const Card = styled.div`
  background: rgba(255,255,255,0.04);
  padding: 14px;
  border-radius: 10px;
  margin-bottom: 12px;
`;

export default function History() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const r = ref(database, 'readings');
    const unsub = onValue(r, snapshot => {
      const v = snapshot.val();
      if (!v) { setItems([]); return; }
      const arr = Object.entries(v).map(([id, val]) => ({ id, ...val }));
      arr.sort((a,b) => new Date(b.timestamp) - new Date(a.timestamp));
      setItems(arr);
    });
    return () => unsub();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this entry?')) return;
    await remove(ref(database, `readings/${id}`));
  };

  return (
    <Page>
      <h1>Historical Readings</h1>
      {items.length === 0 && <div>No saved readings.</div>}
      {items.map(it => (
        <Card key={it.id}>
          <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center' }}>
            <div>
              <div style={{ fontWeight:800 }}>{it.source || 'unknown'}</div>
              <div style={{ color:'rgba(0,0,0,0.6)', fontSize:13 }}>{new Date(it.timestamp).toLocaleString()}</div>
            </div>

            <div style={{ display:'flex', gap:10, alignItems:'center' }}>
              <div style={{ background:'#10b981', color:'#fff', padding:'8px 12px', borderRadius:8, fontWeight:800 }}>{it.pollutionLabel}</div>
              <button onClick={() => handleDelete(it.id)} style={{ background:'#ef4444', color:'#fff', border:'none', padding:'8px 10px', borderRadius:8 }}>Delete</button>
            </div>
          </div>

          <div style={{ marginTop:10, display:'flex', gap:18 }}>
            <div>Temp: <strong>{Number(it.temperature || 0).toFixed(2)} Â°C</strong></div>
            <div>TDS: <strong>{Number(it.tds || 0).toFixed(2)} ppm</strong></div>
            <div>Turbidity: <strong>{Number(it.turbidity || 0).toFixed(2)} NTU</strong></div>
            <div>DO: <strong>{Number(it.dissolvedOxygen || 0).toFixed(2)} mg/L</strong></div>
            <div>pH: <strong>{Number(it.pH || 7).toFixed(2)}</strong></div>
          </div>
        </Card>
      ))}
    </Page>
  );
}
